// export default Login;
import { useForm } from 'react-hook-form';
import { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [role, setRole] = useState('user');
  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = async (data) => {
    try {
      const response = await axios.post(
        'http://localhost:3001/api/auth/login',
        {
          ...data,
          role,
        },
        {
          withCredentials: true,
        }
      );

      if (response.status === 200) {
        const userData = response.data;
        localStorage.setItem('user', JSON.stringify(userData));

        if (userData.role === 'admin' && role === 'admin') {
          alert('Admin login successful');
          navigate('/admin/dashboard');
        } else if (userData.role === 'user' && role === 'user') {
          alert('User login successful');
          navigate('/');
        } else {
          alert(`You are not authorized to login as ${role}`);
        }
      }
    } catch (err) {
      alert(err?.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div style={containerStyle}>
      <form onSubmit={handleSubmit(onSubmit)} style={formStyle}>
        <h2 style={headingStyle}>Login to StyleHub</h2>

        <div style={roleContainerStyle}>
          <label>
            <input
              type="radio"
              value="user"
              checked={role === 'user'}
              onChange={() => setRole('user')}
            />
            {' '}User
          </label>
          <label>
            <input
              type="radio"
              value="admin"
              checked={role === 'admin'}
              onChange={() => setRole('admin')}
            />
            {' '}Admin
          </label>
        </div>

        {/* Email Input */}
        <input
          type="email"
          placeholder="Email"
          style={inputStyle}
          {...register('email', {
            required: 'Email is required',
            pattern: {
              value: /^\S+@\S+\.\S+$/,
              message: 'Enter a valid email address',
            },
          })}
        />
        {errors.email && <p style={errorText}>{errors.email.message}</p>}

        {/* Password Input with Validation */}
        <input
          type="password"
          placeholder="Password"
          style={inputStyle}
          {...register('password', {
            required: 'Password is required',
            minLength: {
              value: 6,
              message: 'Password must be at least 6 characters',
            },
            pattern: {
              value: /^(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*()_+\-=[\]{};':"\\|,.<>/?]).{6,}$/,
              message:
                'Password must include uppercase, number, and special character',
            },
          })}
        />
        <p style={hintText}>
          Must be at least 6 characters, include 1 uppercase letter, 1 number, and 1 special character
        </p>
        {errors.password && <p style={errorText}>{errors.password.message}</p>}

        {/* Phone Number Input */}
        <input
          type="text"
          placeholder="Phone Number"
          style={inputStyle}
          {...register('phone', {
            required: 'Phone number is required',
            pattern: {
              value: /^[6-9]\d{9}$/,
              message: 'Enter a valid 10-digit phone number starting with 6-9',
            },
          })}
        />
        {errors.phone && <p style={errorText}>{errors.phone.message}</p>}

        <button type="submit" style={submitBtn}>Login</button>

        <p style={textCenter}>
          Don't have an account?{' '}
          <Link to="/register" style={linkStyle}>Register</Link>
        </p>

        <p style={{ textAlign: 'center', marginTop: '8px' }}>
          <span
            onClick={() => navigate('/')}
            style={skipLink}
            onMouseEnter={(e) => e.target.style.color = '#3b82f6'}
            onMouseLeave={(e) => e.target.style.color = '#6b7280'}
          >
            Don't want to create an account right now?
          </span>
        </p>
      </form>
    </div>
  );
};

// ðŸ”§ Styles
const containerStyle = {
  backgroundColor: '#f9fafb',
  minHeight: '100vh',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  padding: '20px',
  fontFamily: 'system-ui, sans-serif',
};

const formStyle = {
  backgroundColor: '#ffffff',
  padding: '30px',
