import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useForm } from 'react-hook-form';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [editingProductId, setEditingProductId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredProducts, setFilteredProducts] = useState([]);

  const { register, handleSubmit, setValue, reset } = useForm();

  useEffect(() => {
    fetchProducts();
  }, []);

  useEffect(() => {
    const filtered = products.filter((product) =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredProducts(filtered);
  }, [searchTerm, products]);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/products');
      setProducts(res.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const onSubmit = async (data) => {
    setLoading(true);
    try {
      if (editingProductId) {
        await axios.put(`http://localhost:3001/api/products/${editingProductId}`, data);
        setEditingProductId(null);
      } else {
        await axios.post('http://localhost:3001/api/products/add', data);
      }
      fetchProducts();
      reset();
    } catch (error) {
      console.error('Error submitting product:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (product) => {
    setEditingProductId(product._id);
    setValue('name', product.name);
    setValue('size', product.size);
    setValue('style', product.style);
    setValue('category', product.category);
    setValue('color', product.color);
    setValue('price', product.price);
    setValue('quantity', product.quantity);
    setValue('image', product.image);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/products/${id}`);
      fetchProducts();
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div
      style={{
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '20px',
        fontFamily: 'Arial, sans-serif',
      }}
    >
      <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>üõ†Ô∏è Admin Dashboard</h1>

      <form
        onSubmit={handleSubmit(onSubmit)}
        style={{
          display: 'grid',
          gap: '10px',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          marginBottom: '20px',
        }}
      >
        <input placeholder="Name" {...register('name', { required: true })} />
        <input placeholder="Size" {...register('size', { required: true })} />
        <input placeholder="Style" {...register('style', { required: true })} />
        <input placeholder="Category" {...register('category', { required: true })} />
        <input placeholder="Color" {...register('color', { required: true })} />
        <input type="number" placeholder="Price" {...register('price', { required: true })} />
        <input type="number" placeholder="Quantity" {...register('quantity', { required: true })} />
        <input placeholder="Image URL" {...register('image', { required: true })} />
        <button type="submit" disabled={loading} style={{ gridColumn: 'span 2' }}>
          {editingProductId ? 'Update Product' : 'Add Product'}
        </button>
      </form>

      <input
        type="text"
        placeholder="Search products..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{
          width: '100%',
          marginBottom: '20px',
          padding: '10px',
          fontSize: '16px',
        }}
      />

      <div style={{ overflowX: 'auto' }}>
        <table
          style={{
            width: '100%',
            borderCollapse: 'collapse',
            minWidth: '900px',
          }}
        >
          <thead>
            <tr>
              <th style={thStyle}>Name</th>
              <th style={thStyle}>Size</th>
              <th style={thStyle}>Style</th>
              <th style={thStyle}>Category</th>
              <th style={thStyle}>Color</th>
              <th style={thStyle}>Price</th>
              <th style={thStyle}>Quantity</th>
              <th style={thStyle}>Image</th>
              <th style={thStyle}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredProducts.map((product) => (
              <tr key={product._id}>
                <td style={tdStyle}>{product.name}</td>
                <td style={tdStyle}>{product.size}</td>
                <td style={tdStyle}>{product.style}</td>
                <td style={tdStyle}>{product.category}</td>
                <td style={tdStyle}>{product.color}</td>
                <td style={tdStyle}>‚Çπ{product.price}</td>
                <td style={tdStyle}>{product.quantity}</td>
                <td style={tdStyle}>
                  <img
                    src={product.image}
                    alt={product.name}
                    style={{ width: '50px', height: '50px', objectFit: 'cover' }}
                  />
                </td>
                <td style={tdStyle}>
                  <button onClick={() => handleEdit(product)} style={actionBtnStyle}>
                    ‚úèÔ∏è
                  </button>
                  <button onClick={() => handleDelete(product._id)} style={actionBtnStyle}>
                    üóëÔ∏è
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

const thStyle = {
  border: '1px solid #ccc',
  padding: '10px',
  backgroundColor: '#f0f0f0',
  textAlign: 'left',
};

const tdStyle = {
  border: '1px solid #ccc',
  padding: '10px',
  textAlign: 'left',
};

const actionBtnStyle = {
  marginRight: '10px',
  padding: '6px 8px',
  borderRadius: '4px',
  cursor: 'pointer',
};

export default AdminDashboard;
