import React from 'react';

const ProductGrid = ({ products, category, size, style, onAddToCart }) => {
  // ✅ Filter products by category, size, and style
  const filteredProducts = products.filter((product) => {
    const matchesCategory = category ? product.category === category : true;
    const matchesSize = size ? product.size === size : true;
    const matchesStyle = style ? product.style === style : true;
    return matchesCategory && matchesSize && matchesStyle;
  });

  return (
    <div
      style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
        gap: '20px',
        marginTop: '20px',
      }}
    >
      {filteredProducts.map((product) => (
        <div
          key={product._id}
          style={{
            backgroundColor: '#fff',
            padding: '16px',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
            textAlign: 'center',
          }}
        >
          <h3 style={{ marginBottom: '8px' }}>{product.name}</h3>
          <p style={{ marginBottom: '4px' }}>Size: {product.size}</p>
          <p style={{ marginBottom: '4px' }}>Style: {product.style}</p>
          <p style={{ marginBottom: '4px' }}>Price: ${product.price}</p>
          <button
            onClick={() => onAddToCart(product)}
            style={{
              marginTop: '8px',
              padding: '8px 12px',
              backgroundColor: '#2563eb',
              color: '#fff',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
            }}
          >
            Add to Cart
          </button>
        </div>
      ))}
    </div>
  );
};

export default ProductGrid;
