import React from 'react';

const ProductGrid = ({ products = [], category, size, style, onAddToCart }) => {
  const filteredProducts = products.filter((product) => {
    const matchCategory = category ? product.category === category : true;
    const matchSize = size ? product.size === size : true;
    const matchStyle = style ? product.style === style : true;

    return matchCategory && matchSize && matchStyle;
  });

  return (
    <div style={{
      display: 'grid',
      gap: '20px',
      gridTemplateColumns: 'repeat(auto-fill, minmax(240px, 1fr))'
    }}>
      {filteredProducts.map((product) => (
        <div
          key={product._id}
          style={{
            padding: '16px',
            background: '#fff',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
            textAlign: 'center',
          }}
        >
          <div
            style={{
              width: '100%',
              height: '160px',
              backgroundColor: product.color || '#e5e7eb',
              borderRadius: '6px',
              marginBottom: '12px',
            }}
          />
          <h4 style={{ fontWeight: '600', marginBottom: '4px' }}>{product.name}</h4>
          <p style={{ marginBottom: '4px', fontSize: '14px' }}><strong>Size:</strong> {product.size}</p>
          <p style={{ marginBottom: '4px', fontSize: '14px' }}><strong>Style:</strong> {product.style}</p>
          <p style={{ fontSize: '14px', fontWeight: '600' }}>${product.price}</p>
          <button
            onClick={() => onAddToCart(product)}
            style={{
              marginTop: '10px',
              backgroundColor: '#3b82f6',
              color: '#fff',
              border: 'none',
              padding: '10px',
              width: '100%',
              borderRadius: '6px',
              fontWeight: '600',
              cursor: 'pointer',
            }}
          >
            Add to Cart
          </button>
        </div>
      ))}
    </div>
  );
};

export default ProductGrid;
