import React, { useState } from 'react';
import Header from '../Components/Header';
import CategoryNav from '../Components/CategoryNav';
import Filters from '../Components/Filters';
import ProductGrid from '../Components/ProductGrid';
import CartSidebar from '../Components/CartSidebar';

const Home = () => {
  const [cartOpen, setCartOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [sizeFilter, setSizeFilter] = useState('');
  const [styleFilter, setStyleFilter] = useState('');
  const [cartItems, setCartItems] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const handleAddToCart = (product) => {
    setSelectedProduct(product); // Show product confirmation modal
  };

  const handleConfirmAdd = () => {
    if (!selectedProduct) return;

    const existing = cartItems.find(
      (item) => item.id === selectedProduct.id
    );

    if (existing) {
      const updated = cartItems.map((item) =>
        item.id === selectedProduct.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
      setCartItems(updated);
    } else {
      setCartItems([...cartItems, { ...selectedProduct, quantity: 1 }]);
    }

    setCartOpen(true);
    setSelectedProduct(null);
  };

  return (
    <div style={{ fontFamily: 'sans-serif', background: '#f9fafb' }}>
      <Header cartItems={cartItems} onCartToggle={() => setCartOpen(true)} />
      <CategoryNav onCategorySelect={setSelectedCategory} />
      <main style={{ maxWidth: '1200px', margin: '0 auto', padding: '20px' }}>
        <Filters
          sizeFilter={sizeFilter}
          setSizeFilter={setSizeFilter}
          styleFilter={styleFilter}
          setStyleFilter={setStyleFilter}
        />
        <ProductGrid
          category={selectedCategory}
          size={sizeFilter}
          style={styleFilter}
          onAddToCart={handleAddToCart}
        />
      </main>

      <CartSidebar
        open={cartOpen}
        onClose={() => setCartOpen(false)}
        cartItems={cartItems}
        setCartItems={setCartItems}
      />

      {/* Product Confirmation Modal */}
      {selectedProduct && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0,0,0,0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 1000,
          }}
        >
          <div
            style={{
              background: '#fff',
              padding: '20px',
              borderRadius: '8px',
              textAlign: 'center',
              width: '320px',
            }}
          >
            <h3>{selectedProduct.name}</h3>
            <p><strong>Size:</strong> {selectedProduct.size}</p>
            <p><strong>Price:</strong> ${selectedProduct.price}</p>

            <button
              onClick={handleConfirmAdd}
              style={{
                marginTop: '20px',
                backgroundColor: '#22c55e',
                color: '#fff',
                padding: '8px 16px',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              Confirm
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
