// Redesigned CartSidebar.jsx with layout similar to "Kettle & Fire" style
import React from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';

const CartSidebar = ({ open, onClose, cartItems, setCartItems }) => {
  if (!open) return null;

  const updateQuantity = (id, size, name, delta) => {
    const updated = cartItems.map(item =>
      item.id === id && item.size === size && item.name === name
        ? { ...item, quantity: Math.max(1, item.quantity + delta) }
        : item
    );
    setCartItems(updated);
  };

  const removeItem = (id, size, name) => {
    setCartItems(cartItems.filter(item => !(item.id === id && item.size === size && item.name === name)));
  };

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleSaveCart = async () => {
    const stored = localStorage.getItem('user');
    if (!stored) {
      alert("User not logged in");
      return;
    }

    try {
      await axios.post(
        'http://localhost:3001/api/cart/save',
        { cartItems, total },
        { withCredentials: true }
      );

      alert('Cart saved successfully!');
      setCartItems([]);
      onClose();
    } catch (err) {
      console.error('❌ Failed to save cart:', err);
      alert('Failed to save cart');
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-40 flex justify-end">
      <div className="w-[380px] h-full bg-white shadow-xl p-6 overflow-y-auto relative">
        <div className="flex justify-between items-center mb-4 border-b pb-3">
          <h2 className="text-lg font-semibold">Your Cart</h2>
          <button onClick={onClose} className="text-red-600 text-3xl font-bold">×</button>
        </div>

        {cartItems.length === 0 ? (
          <p className="text-center text-gray-600">Your cart is empty.</p>
        ) : (
          cartItems.map((item, index) => (
            <div key={`${item.id}-${item.size}-${item.name}-${index}`} className="mb-5 border-b pb-4">
              <div className="flex gap-4 items-center">
                <img
                  src={item.image}
                  alt={item.name}
                  className="w-16 h-16 object-cover rounded"
                />
                <div className="flex-1">
                  <p className="font-semibold text-sm">{item.name}</p>
                  <p className="text-xs text-gray-500">Size: <strong>{item.size}</strong></p>
                  <p className="text-xs text-gray-500">Style: <strong>{item.style}</strong></p>
                  <p className="text-sm font-medium">₹{item.price.toFixed(2)}</p>
                </div>
              </div>

              <div className="flex items-center mt-2 gap-2">
                <button onClick={() => updateQuantity(item.id, item.size, item.name, -1)} className="w-8 h-8 text-sm border rounded">-</button>
                <span>{item.quantity}</span>
                <button onClick={() => updateQuantity(item.id, item.size, item.name, 1)} className="w-8 h-8 text-sm border rounded">+</button>

                <button
                  onClick={() => removeItem(item.id, item.size, item.name)}
                  className="ml-auto text-red-600 hover:text-red-800"
                >
                  <FontAwesomeIcon icon={faTrash} />
                </button>
              </div>
            </div>
          ))
        )}

        {cartItems.length > 0 && (
          <div className="border-t pt-4">
            <div className="flex justify-between font-semibold text-lg mb-4">
              <span>Total</span>
              <span>₹{total.toFixed(2)}</span>
            </div>
            <button
              className="bg-blue-600 hover:bg-blue-700 text-white py-2 w-full rounded font-semibold"
              onClick={handleSaveCart}
            >
              Add to Cart
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CartSidebar;
