import React from 'react';

const categories = ['Shirts', 'Pants', 'Shoes', 'Accessories'];

const CategoryNav = ({ onCategorySelect, selectedCategory }) => {
  return (
    <nav style={{ display: 'flex', gap: '1rem', borderBottom: '1px solid #eee', padding: '1rem' }}>
      {categories.map((cat) => (
        <button
          key={cat}
          onClick={() => onCategorySelect(cat)}
          style={{
            background: 'none',
            border: 'none',
            cursor: 'pointer',
            fontWeight: 'bold',
            paddingBottom: '4px',
            borderBottom: selectedCategory === cat ? '2px solid blue' : 'none',
            color: selectedCategory === cat ? 'blue' : 'black',
            transition: 'all 0.2s ease-in-out'
          }}
        >
          {cat}
        </button>
      ))}
    </nav>
  );
};

export default CategoryNav;
