import React from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';

const CartSidebar = ({ open, onClose, cartItems, setCartItems }) => {
  if (!open) return null;

  const updateQuantity = (id, size, name, delta) => {
    const updated = cartItems.map(item =>
      item.id === id && item.size === size && item.name === name
        ? { ...item, quantity: Math.max(1, item.quantity + delta) }
        : item
    );
    setCartItems(updated);
  };

  const removeItem = (id, size, name) => {
    setCartItems(cartItems.filter(item => !(item.id === id && item.size === size && item.name === name)));
  };

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleCheckout = async () => {
    const stored = localStorage.getItem('user');
    if (!stored) {
      alert("User not logged in");
      return;
    }

    try {
      await axios.post(
        'http://localhost:3001/api/cart/save',
        { cartItems, total },
        { withCredentials: true }
      );

      alert('Cart saved successfully!');
      setCartItems([]);
      onClose();
    } catch (err) {
      console.error('❌ Failed to save cart:', err);
      alert('Failed to save cart');
    }
  };

  return (
    <div style={overlayStyle}>
      <div style={sidebarStyle}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
          <h3 style={{ margin: 0 }}>My Cart</h3>
          <button onClick={onClose} style={closeBtn}>×</button>
        </div>

        {cartItems.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          cartItems.map((item, index) => (
            <div key={`${item.id}-${item.size}-${item.name}-${index}`} style={itemStyle}>
              <div style={productRow}>
                <img
                  src={item.image}
                  alt={item.name}
                  style={productImage}
                />
                <div>
                  <p style={nameText}>{item.name}</p>
                  <p style={text}><strong>Size:</strong> {item.size}</p>
                  <p style={text}><strong>Style:</strong> {item.style}</p>
                  <p style={text}><strong>₹</strong>{item.price.toFixed(2)}</p>
                </div>
              </div>

              <div style={quantityRow}>
                <button onClick={() => updateQuantity(item.id, item.size, item.name, -1)} style={qtyBtn}>-</button>
                <span style={qtyCount}>{item.quantity}</span>
                <button onClick={() => updateQuantity(item.id, item.size, item.name, 1)} style={qtyBtn}>+</button>

                <button onClick={() => removeItem(item.id, item.size, item.name)} style={deleteBtn}>
                  <FontAwesomeIcon icon={faTrash} style={{ color: 'red' }} />
                </button>
              </div>
            </div>
          ))
        )}

        <div style={{ marginTop: '20px' }}>
          <h4 style={totalText}>Total: ₹{total.toFixed(2)}</h4>
          <button style={checkoutBtn} onClick={handleCheckout}>
            Checkout
          </button>
        </div>
      </div>
    </div>
  );
};

