import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '',
    size: '',
    style: '',
    category: '',
    price: '',
    quantity: '',
    image: '',
    status: 'active',
  });

  const [totalProducts, setTotalProducts] = useState(0);
  const [activeProducts, setActiveProducts] = useState(0);
  const [lowStock, setLowStock] = useState(0);
  const [totalValue, setTotalValue] = useState(0);

  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await axios.get('http://localhost:3001/api/products/all');
    setProducts(res.data);
    calculateStats(res.data);
  };

  const calculateStats = (products) => {
    setTotalProducts(products.length);
    setActiveProducts(products.filter(p => p.status === 'active').length);
    const lowStockCount = products.filter(p => p.quantity < 10).length;
    setLowStock(lowStockCount);
    const total = products.reduce((acc, product) => acc + (product.price * product.quantity), 0);
    setTotalValue(total);
  };

  const handleDeleteAll = async () => {
    try {
      await axios.delete('http://localhost:3001/api/products/delete-all');
      fetchProducts();
    } catch (err) {
      console.error('Delete all failed', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/products/delete/${id}`);
      fetchProducts();
    } catch (err) {
      console.error('Delete failed', err);
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setNewProduct(product);
  };

  const handleInputChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      if (editingProduct) {
        await axios.put(`http://localhost:3001/api/products/update/${editingProduct._id}`, newProduct);
      } else {
        await axios.post('http://localhost:3001/api/products/add', newProduct);
      }
      setEditingProduct(null);
      setNewProduct({ name: '', size: '', style: '', category: '', price: '', quantity: '', image: '', status: 'active' });
      fetchProducts();
    } catch (err) {
      console.error('Save failed', err);
      alert('Something went wrong while saving!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // or whatever key you used
    navigate('/login'); // redirect to login
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#f4f4f4', fontFamily: 'Arial, sans-serif' }}>
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Admin Dashboard</h2>
        <button onClick={handleLogout} style={{ backgroundColor: '#333', color: 'white', padding: '8px 16px', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
          Logout
        </button>
      </div>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '20px', marginBottom: '20px' }}>
        <div style={{ padding: '20px', borderRadius: '10px', background: '#fff', boxShadow: '0 0 10px rgba(0,0,0,0.1)' }}>
          <h4>Total Products</h4>
          <h2>{totalProducts}</h2>
        </div>
        <div style={{ padding: '20px', borderRadius: '10px', background: '#fff', boxShadow: '0 0 10px rgba(0,0,0,0.1)' }}>
          <h4>Active Products</h4>
          <h2>{activeProducts}</h2>
        </div>
        <div style={{ padding: '20px', borderRadius: '10px', background: '#fff', boxShadow: '0 0 10px rgba(0,0,0,0.1)' }}>
          <h4>Low Stock</h4>
          <h2>{lowStock}</h2>
        </div>
        <div style={{ padding: '20px', borderRadius: '10px', background: '#fff', boxShadow: '0 0 10px rgba(0,0,0,0.1)' }}>
          <h4>Total Value</h4>
          <h2>${totalValue.toFixed(2)}</h2>
        </div>
      </div>

      <h3>Product Management</h3>
      <button onClick={() => setEditingProduct(null)} style={{ padding: '10px 20px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '5px', marginBottom: '10px' }}>
        + Add New Product
      </button>
      <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '10px' }}>
        <thead>
          <tr style={{ background: '#007bff', color: 'white' }}>
            <th>Product</th>
            <th>Category</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Size</th>
            <th>Style</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product._id}>
              <td>{product.name}</td>
              <td>{product.category}</td>
              <td>${product.price.toFixed(2)}</td>
              <td>{product.quantity}</td>
              <td>{product.size}</td>
              <td>{product.style}</td>
              <td style={{ color: product.status === 'active' ? 'green' : 'red' }}>
                {product.status}
              </td>
              <td>
                <button onClick={() => handleEdit(product)} style={{ marginRight: '5px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', padding: '5px 10px' }}>Edit</button>
                <button onClick={() => handleDelete(product._id)} style={{ backgroundColor: 'red', color: 'white', border: 'none', borderRadius: '5px', padding: '5px 10px' }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add/Edit form */}
      <div style={{ marginTop: '30px', padding: '20px', background: '#fff', borderRadius: '10px', boxShadow: '0 0 10px rgba(0,0,0,0.1)' }}>
        <h3>{editingProduct ? 'Edit Product' : 'Add Product'}</h3>
        <input name="name" placeholder="Name" value={newProduct.name} onChange={handleInputChange} />
        <input name="category" placeholder="Category" value={newProduct.category} onChange={handleInputChange} />
        <input name="price" placeholder="Price" type="number" value={newProduct.price} onChange={handleInputChange} />
        <input name="quantity" placeholder="Quantity" type="number" value={newProduct.quantity} onChange={handleInputChange} />
        <input name="size" placeholder="Size" value={newProduct.size} onChange={handleInputChange} />
        <input name="style" placeholder="Style" value={newProduct.style} onChange={handleInputChange} />
        <select name="status" value={newProduct.status} onChange={handleInputChange}>
          <option value="active">Active</option>
          <option value="inactive">Inactive</option>
        </select>
        <button onClick={handleSave} style={{ backgroundColor: '#007bff', color: 'white', marginTop: '10px', padding: '10px 20px', border: 'none', borderRadius: '5px' }}>
          {editingProduct ? 'Update' : 'Add'}
        </button>
      </div>
    </div>
  );
};

export default AdminDashboard;
