import React, { useEffect, useState, useRef } from 'react';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]); // Store original data
  const [editingProduct, setEditingProduct] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Add filters state
  const [filters, setFilters] = useState({
    category: '',
    size: '',
    style: ''
  });

  // Add new state for image upload
  const [imagePreview, setImagePreview] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const fileInputRef = useRef(null);

  const [newProduct, setNewProduct] = useState({
    name: '',
    size: '',
    style: '',
    category: '',
    price: '',
    quantity: '',
    image: '',
  });

  const [totalProducts, setTotalProducts] = useState(0);
  const [activeProducts, setActiveProducts] = useState(0);
  const [lowStock, setLowStock] = useState(0);
  const [totalValue, setTotalValue] = useState(0);

  const [showDeletePopup, setShowDeletePopup] = useState(false);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);

  // Check if mobile view
  const [isMobile, setIsMobile] = useState(window.innerWidth < 600);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 600);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    fetchProducts();

    // Close dropdown when clicking outside
    const handleClickOutside = (event) => {
      if (showProfileDropdown && !event.target.closest('.profile-dropdown-container')) {
        setShowProfileDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showProfileDropdown]);

  // Apply filters whenever filters or allProducts change
  useEffect(() => {
    applyFilters();
  }, [filters, allProducts]);

  const fetchProducts = async () => {
    try {
      // Mock data for demonstration
      const data = [
        {
          _id: '1',
          name: 'Cotton T-Shirt',
          category: 'Shirts',
          price: 25.99,
          quantity: 15,
          size: 'M',
          style: 'Regular Fit',
          image: 'https://via.placeholder.com/150/0000FF/808080?Text=Shirt'
        },
        {
          _id: '2',
          name: 'Denim Jeans',
          category: 'Pants',
          price: 59.99,
          quantity: 8,
          size: 'L',
          style: 'Regular Fit',
          image: 'https://via.placeholder.com/150/FF0000/FFFFFF?Text=Jeans'
        },
        {
          _id: '3',
          name: 'Leather Wallet',
          category: 'Accessories',
          price: 39.99,
          quantity: 0,
          size: 'S',
          style: 'Regular Fit',
          image: 'https://via.placeholder.com/150/00FF00/000000?Text=Wallet'
        }
      ];
      setAllProducts(data);
      setProducts(data);
      calculateStats(data);
    } catch (err) {
      console.error('Failed to fetch products', err);
    }
  };

  // New function to apply filters
  const applyFilters = () => {
    let filtered = [...allProducts];

    if (filters.category) {
      filtered = filtered.filter(product => product.category === filters.category);
    }

    if (filters.size) {
      filtered = filtered.filter(product => product.size === filters.size);
    }

    if (filters.style) {
      filtered = filtered.filter(product => product.style === filters.style);
    }

    setProducts(filtered);
    calculateStats(filtered);
  };

  // New function to handle filter changes
  const handleFilterChange = (filterType, value) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: value
    }));
  };

  const calculateStats = (products) => {
    setTotalProducts(products.length);
    const active = products.filter((p) => p.quantity >= 1);
    setActiveProducts(active.length);
    const lowStockCount = products.filter((p) => p.quantity > 0 && p.quantity < 10).length;
    setLowStock(lowStockCount);
    const total = products.reduce((acc, product) => acc + product.price * product.quantity, 0);
    setTotalValue(total);
  };

  const handleDeleteClick = (id) => {
    setSelectedProductId(id);
    setShowDeletePopup(true);
  };

  const confirmDelete = async () => {
    try {
      // Mock delete - in real app, make API call
      const updatedProducts = allProducts.filter(p => p._id !== selectedProductId);
      setAllProducts(updatedProducts);
      setProducts(updatedProducts);
      calculateStats(updatedProducts);
    } catch (err) {
      console.error('Delete failed', err);
    } finally {
      setShowDeletePopup(false);
      setSelectedProductId(null);
    }
  };

  const cancelDelete = () => {
    setShowDeletePopup(false);
    setSelectedProductId(null);
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setNewProduct(product);
    setShowModal(true);
    // Clear image upload states when editing
    setImagePreview(null);
    setSelectedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Updated handleInputChange to clear file upload when URL is entered
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });

    // If image URL is being entered, clear file upload
    if (name === 'image' && value) {
      setImagePreview(null);
      setSelectedFile(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  // New image upload handlers
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file type
      const validTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif', 'image/webp'];
      if (!validTypes.includes(file.type)) {
        alert('Please select a valid image file (JPEG, PNG, GIF, WebP)');
        return;
      }

      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        alert('File size should be less than 5MB');
        return;
      }

      setSelectedFile(file);

      // Create preview URL
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);

      // Clear URL input when file is uploaded
      setNewProduct(prev => ({ ...prev, image: '' }));
    }
  };

  const removeImage = () => {
    setImagePreview(null);
    setSelectedFile(null);
    setNewProduct(prev => ({ ...prev, image: '' }));
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Updated handleSave to handle file uploads
  const handleSave = async () => {
    try {
      let imageUrl = newProduct.image;

      // If there's a selected file, upload it first
      if (selectedFile) {
        // Mock upload - in real app, upload to server
        imageUrl = URL.createObjectURL(selectedFile);
      }

      // Create product data with image URL
      const productData = {
        ...newProduct,
        _id: editingProduct ? editingProduct._id : Date.now().toString(),
        image: imageUrl,
        price: parseFloat(newProduct.price),
        quantity: parseInt(newProduct.quantity)
      };

      let updatedProducts;
      if (editingProduct) {
        updatedProducts = allProducts.map(p => p._id === editingProduct._id ? productData : p);
      } else {
        updatedProducts = [...allProducts, productData];
      }

      setAllProducts(updatedProducts);
      calculateStats(updatedProducts);

      // Reset form and close modal
      setEditingProduct(null);
      setShowModal(false);
      setNewProduct({ name: '', size: '', style: '', category: '', price: '', quantity: '', image: '' });
      setImagePreview(null);
      setSelectedFile(null);
    } catch (err) {
      console.error('Save failed', err);
      alert('Something went wrong while saving!');
    }
  };

  const handleLogout = () => {
    alert('Logout clicked - implement your logout logic here');
  };

  // Updated openModal function
  const openModal = () => {
    setEditingProduct(null);
    setNewProduct({ name: '', size: '', style: '', category: '', price: '', quantity: '', image: '' });
    setImagePreview(null);
    setSelectedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
    setShowModal(true);
  };

  const inputStyle = {
    padding: '10px',
    borderRadius: '4px',
    border: '1px solid #ccc',
    fontSize: '14px',
  };

  return (
    <div style={{ 
      padding: isMobile ? '16px' : '24px', 
      backgroundColor: '#f3f4f6', 
      minHeight: '100vh', 
      fontFamily: 'Arial, sans-serif' 
    }}>
      {/* Topbar */}
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '24px',
        flexWrap: 'wrap',
        gap: '12px'
      }}>
        <h2 style={{ 
          fontSize: isMobile ? '20px' : '24px', 
          fontWeight: 'bold',
          margin: '0'
        }}>
          <span role="img" aria-label="dashboard" style={{ marginRight: '8px' }}>ðŸ“¦</span>Admin Dashboard
        </h2>
        <div style={{ display: 'flex', alignItems: 'center', gap: '12px', position: 'relative' }} className="profile-dropdown-container">
          <span style={{ fontSize: isMobile ? '14px' : '16px' }}>Welcome, Admin</span>
          <div 
            onClick={() => setShowProfileDropdown(!showProfileDropdown)}
            style={{ 
              width: '32px', 
              height: '32px', 
              backgroundColor: '#2563eb', 
              color: '#fff', 
              borderRadius: '50%', 
              display: 'flex', 
              alignItems: 'center', 
              justifyContent: 'center', 
              fontWeight: 'bold',
              cursor: 'pointer'
            }}
          >
            A
          </div>

          {/* Profile Dropdown */}
          {showProfileDropdown && (
            <div style={{
              position: 'absolute',
              top: '100%',
              right: '0',
              marginTop: '8px',
              backgroundColor: '#fff',
              border: '1px solid #e5e7eb',
              borderRadius: '8px',
              boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
              zIndex: 1000,
              minWidth: '120px'
            }}>
              <button
                onClick={handleLogout}
                style={{
                  width: '100%',
                  padding: '12px 16px',
                  backgroundColor: 'transparent',
                  border: 'none',
                  textAlign: 'left',
                  cursor: 'pointer',
                  fontSize: '14px',
                  borderRadius: '8px',
                  color: '#374151'
                }}
                onMouseEnter={(e) => e.target.style.backgroundColor = '#f3f4f6'}
                onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
              >
                ðŸšª Logout
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Stats - Responsive Grid */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: isMobile ? '1fr' : 'repeat(4, 1fr)', 
        gap: '16px', 
        marginBottom: '24px' 
      }}>
        {[
          { label: 'Total Products ðŸ“¦', value: totalProducts },
          { label: 'Active Products âœ…', value: activeProducts },
          { label: 'Low Stock âš ï¸', value: lowStock },
          { label: 'Total Value ðŸ’°', value: `â‚¹${totalValue.toFixed(2)}/-` }
        ].map((stat, idx) => (
          <div key={idx} style={{ 
            backgroundColor: '#fff', 
            padding: isMobile ? '12px' : '16px', 
            borderRadius: '8px' 
          }}>
            <p style={{ 
              margin: '0 0 8px 0', 
              fontSize: isMobile ? '14px' : '16px' 
            }}>{stat.label}</p>
            <h2 style={{ 
              fontSize: isMobile ? '20px' : '24px', 
              fontWeight: 'bold',
              margin: '0'
            }}>{stat.value}</h2>
          </div>
        ))}
      </div>

     {/* Product Table */}
     <div style={{ 
       backgroundColor: '#fff', 
       padding: isMobile ? '12px' : '16px', 
       borderRadius: '8px', 
       marginBottom: '24px', 
       overflowX: 'auto' 
     }}>
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          marginBottom: '16px',
          flexDirection: isMobile ? 'column' : 'row',
          gap: isMobile ? '12px' : '0'
        }}>
          <h3 style={{ 
            fontSize: isMobile ? '24px' : '30px', 
            fontWeight: 'bold',
            margin: '0',
            padding: '8px 12px', 
          }}>Product Management</h3>
          <button
            onClick={openModal}
            style={{ 
              backgroundColor: '#2563eb', 
              color: '#fff', 
              padding: isMobile ? '10px 16px' : '12px 12px', 
              borderRadius: '8px',
              border: 'none',
              cursor: 'pointer',
              fontSize: isMobile ? '16px' : '20px',
              transition: 'all 0.2s ease-in-out',
              transform: 'scale(1)',
              alignSelf: isMobile ? 'flex-start' : 'auto'
            }}
            onMouseEnter={(e) => {
              e.target.style.backgroundColor = '#1d4ed8';
              e.target.style.transform = 'scale(1.05)';
