import React, { useState } from 'react';

const Filters = ({ sizeFilter, setSizeFilter, styleFilter, setStyleFilter }) => {
  const sizes = ['XS', 'S', 'M', 'L', 'XL'];
  const styles = ['Regular Fit', 'Oversized Fit'];

  const [hoveredSize, setHoveredSize] = useState('');
  const [hoveredStyle, setHoveredStyle] = useState('');

  const baseButtonStyle = {
    margin: '4px',
    padding: '6px 14px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
  };

  const getButtonStyle = (isActive, isHovered) => {
    if (isActive) {
      return {
        ...baseButtonStyle,
        backgroundColor: '#1f2937',
        color: '#fff',
        borderColor: '#1f2937',
      };
    } else if (isHovered) {
      return {
        ...baseButtonStyle,
        backgroundColor: '#e6e6e6',
      };
    } else {
      return {
        ...baseButtonStyle,
        backgroundColor: '#fff',
        color: '#000',
      };
    }
  };

  return (
    <div
      style={{
        display: 'flex',
        gap: '3rem',
        backgroundColor: '#f9fafb',
        padding: '1.5rem',
        borderRadius: '10px',
        margin: '2rem auto',
        width: 'fit-content',
        boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)',
      }}
    >
      <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
        <div style={{ fontWeight: 600 }}>Size</div>
        <div style={{ display: 'flex', flexWrap: 'wrap' }}>
          {sizes.map((size) => (
            <button
              key={size}
              style={getButtonStyle(sizeFilter === size, hoveredSize === size)}
              onClick={() => setSizeFilter(sizeFilter === size ? '' : size)}
              onMouseEnter={() => setHoveredSize(size)}
              onMouseLeave={() => setHoveredSize('')}
            >
              {size}
            </button>
          ))}
        </div>
      </div>

      <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
        <div style={{ fontWeight: 600 }}>Style</div>
        <div style={{ display: 'flex', flexWrap: 'wrap' }}>
          {styles.map((style) => (
            <button
              key={style}
              style={getButtonStyle(styleFilter === style, hoveredStyle === style)}
              onClick={() => setStyleFilter(styleFilter === style ? '' : style)}
              onMouseEnter={() => setHoveredStyle(style)}
              onMouseLeave={() => setHoveredStyle('')}
            >
              {style}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Filters;
