// src/components/CartSidebar.jsx
import React from 'react';
import { FaTimes } from 'react-icons/fa';

const CartSidebar = ({ open, onClose, cartItems }) => {
  if (!open) return null;

  return (
    <div className="fixed top-0 right-0 w-[420px] h-full z-50 bg-white shadow-xl transition-transform duration-300 transform translate-x-0">
      {/* Header */}
      <div className="flex justify-between items-center p-4 border-b">
        <h2 className="text-xl font-semibold">MY CART ({cartItems.length})</h2>
        <button onClick={onClose} className="text-gray-600 hover:text-black">
          <FaTimes />
        </button>
      </div>

      {/* Offer Progress */}
      <div className="px-4 py-3 bg-green-50 border-b">
        <p className="text-sm font-medium text-green-800">
          üéâ You've unlocked all prizes
        </p>
        <div className="w-full h-2 bg-gray-200 rounded-full mt-2">
          <div className="h-2 bg-green-500 rounded-full w-full" />
        </div>
        <div className="flex justify-between text-xs text-gray-700 mt-1">
          <span>Free Shipping</span>
          <span>3% off</span>
          <span>6% off</span>
          <span>+ Mystery Gift</span>
        </div>
      </div>

      {/* Items */}
      <div className="p-4 space-y-4 overflow-y-auto max-h-[60vh]">
        {cartItems.map((item, idx) => (
          <div key={idx} className="flex items-start gap-3 border-b pb-3">
            <img src={item.image} alt={item.name} className="w-16 h-20 object-cover" />
            <div className="flex-1">
              <h4 className="font-semibold text-sm">{item.name}</h4>
              <div className="text-gray-500 text-sm">${item.price.toFixed(2)}</div>
              <div className="flex items-center mt-2">
                <button className="border px-2">-</button>
                <span className="px-2">{item.quantity}</span>
                <button className="border px-2">+</button>
              </div>
              <button className="text-red-500 text-xs mt-1">Remove</button>
            </div>
          </div>
        ))}
      </div>

      {/* Related Item Suggestion */}
      <div className="px-4 py-3 bg-gray-50 border-t border-b">
        <p className="text-sm font-semibold text-gray-700">Your order goes great with</p>
        <div className="flex justify-between items-center mt-2">
          <div>
            <p className="text-sm text-gray-800">Turmeric Ginger Bone Broth</p>
            <p className="text-sm text-gray-600">$7.99</p>
          </div>
          <button className="border px-3 py-1 rounded text-sm">Add to Order</button>
        </div>
      </div>

      {/* Footer */}
      <div className="px-4 py-3 flex justify-between items-center border-t">
        <p className="text-lg font-semibold">Subtotal: <span className="text-black">$232.18</span></p>
        <button className="bg-red-600 text-white px-5 py-2 rounded hover:bg-red-700 transition">Checkout</button>
      </div>
    </div>
  );
};

export default CartSidebar;
