import React, { useState } from 'react';
import Header from '../Components/Header';
import CategoryNav from '../Components/CategoryNav';
import Filters from '../Components/Filters';
import ProductGrid from '../Components/ProductGrid';
import CartSidebar from '../Components/CartSidebar';

const Home = () => {
  const [cartOpen, setCartOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [cartItems, setCartItems] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const handleAddToCart = (product) => {
    setSelectedProduct(product); // Open size selection modal
  };

  const handleSizeSelect = (size) => {
    if (!selectedProduct) return;

    const productWithSize = { ...selectedProduct, size };
    const existing = cartItems.find(
      (item) => item.id === productWithSize.id && item.size === size
    );

    if (existing) {
      const updated = cartItems.map((item) =>
        item.id === productWithSize.id && item.size === size
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
      setCartItems(updated);
    } else {
      setCartItems([...cartItems, { ...productWithSize, quantity: 1 }]);
    }

    setCartOpen(true);
    setSelectedProduct(null); // Close modal
  };

  return (
    <div style={{ fontFamily: 'sans-serif', background: '#f9fafb' }}>
      <Header cartItems={cartItems} onCartToggle={() => setCartOpen(true)} />
      <CategoryNav onCategorySelect={setSelectedCategory} />
      <main style={{ maxWidth: '1200px', margin: '0 auto', padding: '20px' }}>
        <Filters category={selectedCategory} />
        <ProductGrid
          category={selectedCategory}
          onAddToCart={handleAddToCart}
        />
      </main>
      <CartSidebar
        open={cartOpen}
        onClose={() => setCartOpen(false)}
        cartItems={cartItems}
        setCartItems={setCartItems}
      />

      {selectedProduct && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0,0,0,0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 1000,
          }}
        >
          <div
            style={{
              background: '#fff',
              padding: '20px',
              borderRadius: '8px',
              textAlign: 'center',
              width: '300px',
            }}
          >
            <h3>Select Size for {selectedProduct.name}</h3>
            <div
              style={{
                display: 'flex',
                gap: '10px',
                marginTop: '15px',
                justifyContent: 'center',
                flexWrap: 'wrap',
              }}
            >
              {['XS', 'S', 'M', 'L', 'XL'].map((size) => (
                <button
                  key={size}
                  onClick={() => handleSizeSelect(size)}
                  style={{
                    padding: '8px 16px',
                    border: '1px solid #ccc',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    backgroundColor: '#f0f0f0',
                  }}
                >
                  {size}
                </button>
              ))}
            </div>
            <button
              onClick={() => setSelectedProduct(null)}
              style={{
                marginTop: '20px',
                backgroundColor: '#f87171',
                color: '#fff',
                padding: '8px 16px',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
