// src/Components/ProductGrid.js
import React from 'react';
import products from '../assets/product';

const ProductGrid = ({ category, size, style, onAddToCart }) => {
  // Filter based on category, size, and style
  const filteredProducts = products.filter((product) => {
    const matchCategory = category ? product.category === category : true;
    const matchSize = size ? product.sizes.includes(size) : true;
    const matchStyle = style ? product.style === style : true;
    return matchCategory && matchSize && matchStyle;
  });

  return (
    <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
      {filteredProducts.length === 0 ? (
        <p>No products match the selected filters.</p>
      ) : (
        filteredProducts.map((product) => (
          <div
            key={product.id}
            style={{
              border: '1px solid #ccc',
              borderRadius: '8px',
              padding: '16px',
              width: '200px',
              textAlign: 'center',
            }}
          >
            <div
              style={{
                backgroundColor: product.color,
                height: '150px',
                marginBottom: '10px',
              }}
            ></div>
            <h4>{product.name}</h4>
            <p style={{ fontWeight: 'bold' }}>${product.price}</p>
            <button
              onClick={() => onAddToCart(product)}
              style={{
                backgroundColor: '#2563eb',
                color: '#fff',
                border: 'none',
                padding: '8px 12px',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              Add to Cart
            </button>
          </div>
        ))
      )}
    </div>
  );
};

export default ProductGrid;
