          }
          .product-table-row {
            transition: background-color 0.2s ease-in-out;
          }
        `}
      </style>

      {/* Updated Modal with Mobile Responsiveness */}
      {showModal && (
        <div style={{
          position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh',
          backgroundColor: 'rgba(0,0,0,0.4)',
          display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000,
          padding: isMobile ? '16px' : '0'
        }}>
          <div style={{
            backgroundColor: '#fff', 
            padding: isMobile ? '16px' : '24px', 
            borderRadius: '8px',
            width: isMobile ? '100%' : '400px',
            maxWidth: isMobile ? 'none' : '400px',
            maxHeight: '90vh', 
            overflowY: 'auto', 
            boxShadow: '0 10px 25px rgba(0,0,0,0.1)'
          }}>
            <h3 style={{ 
              fontSize: isMobile ? '16px' : '18px', 
              fontWeight: 'bold', 
              marginBottom: '16px',
              margin: '0 0 16px 0'
            }}>
              {editingProduct ? 'Edit Product' : 'Add New Product'}
            </h3>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
              <input 
                name="name" 
                placeholder="Product Name" 
                value={newProduct.name} 
                onChange={handleInputChange} 
                style={{
                  ...inputStyle, 
                  width: '100%', 
                  boxSizing: 'border-box',
                  fontSize: isMobile ? '16px' : '14px',
                  padding: isMobile ? '12px' : '10px'
                }} 
              />

              {/* Image Upload Section */}
              <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
                <label style={{ 
                  fontSize: isMobile ? '13px' : '14px', 
                  fontWeight: '500', 
                  color: '#374151' 
                }}>
                  Product Image
                </label>

                {/* Image Preview */}
                {(newProduct.image || imagePreview) && (
                  <div style={{ position: 'relative', width: '100px', height: '100px', marginBottom: '8px' }}>
                    <img 
                      src={imagePreview || newProduct.image} 
                      alt="Product preview" 
                      style={{ 
                        width: '100%', 
                        height: '100%', 
                        objectFit: 'cover', 
                        borderRadius: '4px',
                        border: '1px solid #d1d5db'
                      }} 
                    />
                    <button
                      type="button"
                      onClick={removeImage}
                      style={{
                        position: 'absolute',
                        top: '-8px',
                        right: '-8px',
                        backgroundColor: '#ef4444',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '50%',
                        width: '20px',
                        height: '20px',
                        fontSize: '12px',
                        cursor: 'pointer'
                      }}
                    >
                      ×
                    </button>
                  </div>
                )}

                {/* File Upload Input */}
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  style={{ display: 'none' }}
                  ref={fileInputRef}
                />

                <button
                  type="button"
                  onClick={() => fileInputRef.current?.click()}
                  style={{
                    padding: isMobile ? '10px 12px' : '8px 12px',
                    backgroundColor: '#f3f4f6',
                    border: '1px solid #d1d5db',
                    borderRadius: '4px',
                    fontSize: isMobile ? '15px' : '14px',
                    cursor: 'pointer',
                    width: '100%'
                  }}
                >
                  📁 Upload Image
                </button>

                {/* Or divider */}
                <div style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  gap: '8px', 
                  fontSize: '12px', 
                  color: '#6b7280',
                  margin: '4px 0' 
                }}>
                  <div style={{ flex: 1, height: '1px', backgroundColor: '#e5e7eb' }}></div>
                  <span>OR</span>
                  <div style={{ flex: 1, height: '1px', backgroundColor: '#e5e7eb' }}></div>
                </div>

                <input 
                  name="image" 
                  placeholder="Or paste image URL" 
                  value={newProduct.image} 
                  onChange={handleInputChange} 
                  style={{
                    ...inputStyle, 
                    width: '100%', 
                    boxSizing: 'border-box',
                    fontSize: isMobile ? '16px' : '14px',
                    padding: isMobile ? '12px' : '10px'
                  }} 
                />
              </div>

              <select 
                name="category" 
                value={newProduct.category} 
                onChange={handleInputChange} 
                style={{
                  ...inputStyle, 
                  width: '100%', 
                  boxSizing: 'border-box',
                  fontSize: isMobile ? '16px' : '14px',
                  padding: isMobile ? '12px' : '10px'
                }}
              >
                <option value="">Select Category</option>
                <option value="Shirts">Shirts</option>
                <option value="Pants">Pants</option>
                <option value="Accessories">Accessories</option>
                <option value="Shoes">Shoes</option>
              </select>
              <input 
                name="price" 
                type="number" 
                placeholder="Price ($)" 
                value={newProduct.price} 
                onChange={handleInputChange} 
                style={{
                  ...inputStyle, 
                  width: '100%', 
                  boxSizing: 'border-box',
                  fontSize: isMobile ? '16px' : '14px',
                  padding: isMobile ? '12px' : '10px'
                }} 
              />
              <input 
                name="quantity" 
                type="number" 
                placeholder="Stock Quantity" 
                value={newProduct.quantity} 
                onChange={handleInputChange} 
                style={{
                  ...inputStyle, 
                  width: '100%', 
                  boxSizing: 'border-box',
                  fontSize: isMobile ? '16px' : '14px',
                  padding: isMobile ? '12px' : '10px'
                }} 
              />
              <select 
                name="size" 
                value={newProduct.size} 
                onChange={handleInputChange} 
                style={{
                  ...inputStyle, 
                  width: '100%', 
                  boxSizing: 'border-box',
                  fontSize: isMobile ? '16px' : '14px',
                  padding: isMobile ? '12px' : '10px'
                }}
              >
                <option value="">Select Size</option>
                <option value="XS">XS</option>
                <option value="S">S</option>
                <option value="M">M</option>
                <option value="L">L</option>
                <option value="XL">XL</option>
              </select>
              <select 
                name="style" 
                value={newProduct.style} 
                onChange={handleInputChange} 
                style={{
                  ...inputStyle, 
                  width: '100%', 
                  boxSizing: 'border-box',
                  fontSize: isMobile ? '16px' : '14px',
                  padding: isMobile ? '12px' : '10px'
                }}
              >
                <option value="">Select Style</option>
                <option value="Regular Fit">Regular Fit</option>
                <option value="Oversized Fit">Oversized Fit</option>
              </select>
            </div>

            <div style={{ 
              display: 'flex', 
              justifyContent: 'flex-end', 
              marginTop: '20px', 
              gap: '10px',
              flexDirection: isMobile ? 'column' : 'row'
            }}>
              <button 
                onClick={() => setShowModal(false)} 
                style={{ 
                  padding: isMobile ? '12px 16px' : '8px 16px', 
                  backgroundColor: '#f3f4f6', 
                  borderRadius: '4px', 
                  border: 'none', 
                  cursor: 'pointer',
                  fontSize: isMobile ? '16px' : '14px'
                }}
              >
                Cancel
              </button>
              <button 
                onClick={handleSave} 
                style={{ 
                  padding: isMobile ? '12px 16px' : '8px 16px', 
                  backgroundColor: '#2563eb', 
                  color: '#fff', 
                  borderRadius: '4px', 
                  border: 'none', 
                  cursor: 'pointer',
                  fontSize: isMobile ? '16px' : '14px'
                }}
              >
                {editingProduct ? 'Update' : 'Save Product'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirmation Popup - Mobile Responsive */}
      {showDeletePopup && (
        <div style={{
          position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center',
          justifyContent: 'center', zIndex: 2000,
          padding: isMobile ? '16px' : '0'
        }}>
          <div style={{
            background: '#fff', 
            padding: isMobile ? '20px' : '24px 32px', 
            borderRadius: '12px', 
            width: isMobile ? '100%' : '350px',
            maxWidth: isMobile ? 'none' : '350px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.2)', 
            textAlign: 'center', 
            fontFamily: 'system-ui'
          }}>
            <h3 style={{ 
              marginBottom: '10px', 
              margin: '0 0 10px 0',
              fontSize: isMobile ? '18px' : '20px'
            }}>🟠 Delete Product</h3>
            <p style={{ 
              marginBottom: '20px',
              margin: '0 0 20px 0',
              fontSize: isMobile ? '14px' : '16px'
            }}>
              Are you sure you want to delete this product? This action cannot be undone.
            </p>
            <div style={{ 
              display: 'flex', 
              justifyContent: 'center', 
              gap: '12px',
              flexDirection: isMobile ? 'column' : 'row'
            }}>
              <button 
                onClick={cancelDelete} 
                style={{ 
                  padding: isMobile ? '12px 16px' : '8px 16px', 
                  backgroundColor: '#e5e7eb', 
                  border: 'none', 
                  borderRadius: '8px', 
                  cursor: 'pointer',
                  fontSize: isMobile ? '16px' : '14px'
                }}
              >
                Cancel
              </button>
              <button 
                onClick={confirmDelete} 
                style={{ 
                  padding: isMobile ? '12px 16px' : '8px 16px', 
                  backgroundColor: '#ef4444', 
                  color: '#fff', 
                  border: 'none', 
                  borderRadius: '8px', 
                  cursor: 'pointer',
                  fontSize: isMobile ? '16px' : '14px'
                }}
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
