import React from 'react';

const dummyProducts = [
  { id: 1, name: 'Men T-shirt', category: 'Men', price: '$20', image: 'https://via.placeholder.com/150' },
  { id: 2, name: 'Women Dress', category: 'Women', price: '$35', image: 'https://via.placeholder.com/150' },
  { id: 3, name: 'Kids Shirt', category: 'Kids', price: '$15', image: 'https://via.placeholder.com/150' },
  // Add more...
];

const ProductGrid = ({ category, setModalProduct }) => {
  const filtered = category
    ? dummyProducts.filter((p) => p.category === category)
    : dummyProducts;

  return (
    <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
      {filtered.map((product) => (
        <div
          key={product.id}
          className="bg-white p-4 rounded shadow cursor-pointer"
          onClick={() => setModalProduct(product)}
        >
          <img src={product.image} alt={product.name} className="mb-2" />
          <h4 className="text-lg font-semibold">{product.name}</h4>
          <p className="text-gray-600">{product.price}</p>
        </div>
      ))}
    </div>
  );
};

export default ProductGrid;
