import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminProductPage = () => {
  const [products, setProducts] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    size: 'M',
    style: 'Regular Fit',
    category: '',
    price: '',
    quantity: '',
    image: ''
  });

  const [deleteProductId, setDeleteProductId] = useState(null); // ‚õî for delete modal

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await axios.get('/api/products');
    setProducts(res.data);
  };

  const handleDelete = async () => {
    await axios.delete(`/api/products/${deleteProductId}`);
    setDeleteProductId(null); // close modal
    fetchProducts();
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    await axios.post('/api/products', formData);
    fetchProducts();
    setShowForm(false);
    setFormData({
      name: '',
      size: 'M',
      style: 'Regular Fit',
      category: '',
      price: '',
      quantity: '',
      image: ''
    });
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-semibold mb-4">üõ°Ô∏è Admin Dashboard</h1>

      <button
        className="bg-blue-600 text-white px-4 py-2 rounded"
        onClick={() => setShowForm(!showForm)}
      >
        + Add New Product
      </button>

      {showForm && (
        <form onSubmit={handleAddProduct} className="mt-4 bg-white p-4 rounded shadow">
          <input
            className="block mb-2 border px-2 py-1 w-full"
            placeholder="Product Name"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
          />
          <select
            className="block mb-2 border px-2 py-1 w-full"
            value={formData.size}
            onChange={(e) => setFormData({ ...formData, size: e.target.value })}
          >
            {['XS', 'S', 'M', 'L', 'XL'].map((size) => (
              <option key={size}>{size}</option>
            ))}
          </select>
          <select
            className="block mb-2 border px-2 py-1 w-full"
            value={formData.style}
            onChange={(e) => setFormData({ ...formData, style: e.target.value })}
          >
            {['Regular Fit', 'Oversized Fit'].map((style) => (
              <option key={style}>{style}</option>
            ))}
          </select>
          <input
            className="block mb-2 border px-2 py-1 w-full"
            placeholder="Category"
            value={formData.category}
            onChange={(e) => setFormData({ ...formData, category: e.target.value })}
            required
          />
          <input
            className="block mb-2 border px-2 py-1 w-full"
            placeholder="Price"
            type="number"
            value={formData.price}
            onChange={(e) => setFormData({ ...formData, price: e.target.value })}
            required
          />
          <input
            className="block mb-2 border px-2 py-1 w-full"
            placeholder="Quantity"
            type="number"
            value={formData.quantity}
            onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
            required
          />
          <input
            className="block mb-4 border px-2 py-1 w-full"
            placeholder="Image URL"
            value={formData.image}
            onChange={(e) => setFormData({ ...formData, image: e.target.value })}
            required
          />
          <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded">
            Add Product
          </button>
        </form>
      )}

      <div className="mt-6 bg-white p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Product Management</h2>
        <table className="w-full text-left">
          <thead>
            <tr className="border-b">
              <th>Product</th>
              <th>Category</th>
              <th>Price</th>
              <th>Stock</th>
              <th>Size</th>
              <th>Style</th>
              <th>Image</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map((prod) => (
              <tr key={prod._id} className="border-b">
                <td>{prod.name}</td>
                <td>{prod.category}</td>
                <td>${prod.price}</td>
                <td>{prod.quantity}</td>
                <td>{prod.size}</td>
                <td>{prod.style}</td>
                <td>
                  <img src={prod.image} alt="product" className="w-12 h-12 object-cover" />
                </td>
                <td>
                  <button
                    onClick={() => setDeleteProductId(prod._id)}
                    className="text-red-600 px-2"
                  >
                    üóëÔ∏è
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* ‚úÖ Delete Confirmation Popup */}
      {deleteProductId && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow w-[300px] text-center">
            <h2 className="text-lg font-semibold mb-2">‚ö†Ô∏è Delete Product</h2>
            <p className="mb-4 text-sm text-gray-700">
              Are you sure you want to delete this product? This action cannot be undone.
            </p>
            <div className="flex justify-center gap-4">
              <button
                className="bg-gray-300 px-4 py-2 rounded"
                onClick={() => setDeleteProductId(null)}
              >
                Cancel
              </button>
              <button
                className="bg-red-600 text-white px-4 py-2 rounded"
                onClick={handleDelete}
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminProductPage;
