import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UserDetails = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/auth/getUserDetails', {
          withCredentials: true,
        });
        setUser(response.data);
      } catch (error) {
        console.error('Failed to fetch user details:', error);
        navigate('/login');
      }
    };

    fetchUser();
  }, [navigate]);

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:3001/api/auth/logout', {}, {
        withCredentials: true,
      });
      localStorage.clear();
      navigate('/login');
    } catch (error) {
      console.error('Logout failed:', error);
      alert('Logout failed. Please try again.');
    }
  };

  if (!user) return null;

  return (
    <div style={overlayStyle} onClick={() => navigate(-1)}>
      <div style={sidebarStyle} onClick={(e) => e.stopPropagation()}>
        <div style={headerStyle}>
          <h2>User Details</h2>
          <button onClick={() => navigate(-1)} style={closeBtn}>×</button>
        </div>
        <div style={infoStyle}>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Mobile:</strong> {user.mobile}</p>
        </div>
        <button onClick={handleLogout} style={logoutBtn}>Logout</button>
      </div>
    </div>
  );
};

// ✅ Overlay Background
const overlayStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100vw',
  height: '100vh',
  backgroundColor: 'rgba(0, 0, 0, 0.4)',
  zIndex: 9999,
  display: 'flex',
  justifyContent: 'flex-end',
  alignItems: 'stretch',
};

// ✅ Sidebar Panel
const sidebarStyle = {
  width: '350px',
  height: '100%',
  backgroundColor: '#fff',
  padding: '20px',
  boxShadow: '-2px 0 8px rgba(0,0,0,0.2)',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-between',
};

// ✅ Header
const headerStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginBottom: '20px',
};

// ❌ Close Button
const closeBtn = {
  fontSize: '24px',
  background: 'none',
  border: 'none',
  color: 'red',
  cursor: 'pointer',
};

// ✅ Info Section
const infoStyle = {
  flex: 1,
  display: 'flex',
  flexDirection: 'column',
  gap: '10px',
  fontSize: '15px',
};

// ✅ Logout Button
const logoutBtn = {
  backgroundColor: '#ef4444',
  color: 'white',
  padding: '10px',
  border: 'none',
  borderRadius: '6px',
  fontWeight: 'bold',
  cursor: 'pointer',
};

export default UserDetails;
