import React from 'react';

const categories = ['Shirts', 'Pants', 'Shoes', 'Accessories'];

const CategoryNav = ({ onCategorySelect, activeCategory }) => {
  return (
    <nav style={{ 
      display: 'flex', 
      gap: '2rem', 
      borderBottom: '1px solid #e5e7eb', 
      padding: '0 0 0 0',
      backgroundColor: '#ffffff'
    }}>
      {categories.map((cat) => (
        <button
          key={cat}
          onClick={() => onCategorySelect(cat)}
          style={{
            background: 'none',
            border: 'none',
            cursor: 'pointer',
            fontWeight: '500',
            fontSize: '16px',
            color: activeCategory === cat ? '#2563eb' : '#6b7280',
            padding: '16px 0',
            position: 'relative',
            transition: 'color 0.2s ease-in-out',
            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
          }}
          onMouseOver={(e) => {
            if (activeCategory !== cat) {
              e.target.style.color = '#374151';
            }
          }}
          onMouseOut={(e) => {
            if (activeCategory !== cat) {
              e.target.style.color = '#6b7280';
            }
          }}
        >
          {cat}
          {/* Active indicator underline */}
          {activeCategory === cat && (
            <div style={{
              position: 'absolute',
              bottom: '-1px',
              left: '0',
              right: '0',
              height: '2px',
              backgroundColor: '#2563eb',
              borderRadius: '1px'
            }} />
          )}
        </button>
      ))}
    </nav>
  );
};

export default CategoryNav;
