import React from 'react';

const CategoryNav = ({ onCategorySelect }) => {
  const categories = ['All', 'Men', 'Women', 'Kids'];

  return (
    <nav className="flex justify-center gap-4 bg-gray-100 p-2">
      {categories.map((cat) => (
        <button
          key={cat}
          className="px-4 py-1 bg-white border rounded"
          onClick={() => onCategorySelect(cat === 'All' ? '' : cat)}
        >
          {cat}
        </button>
      ))}
    </nav>
  );
};

export default CategoryNav;
