// ✅ src/Components/CategoryNav.js
import React from 'react';

const categories = ['Shirts', 'Pants', 'Shoes', 'Accessories'];

const CategoryNav = ({ onCategorySelect, selectedCategory }) => {
  return (
    <nav style={{
      display: 'flex',
      justifyContent: 'center',
      gap: '32px',
      padding: '20px 0',
      fontFamily: 'system-ui, sans-serif',
      fontWeight: '500',
      fontSize: '15px',
      backgroundColor: '#fff',
      borderBottom: '1px solid #e5e7eb'
    }}>
      {categories.map((cat) => (
        <button
          key={cat}
          onClick={() => onCategorySelect(cat)}
          style={{
            background: 'none',
            border: 'none',
            cursor: 'pointer',
            paddingBottom: '4px',
            borderBottom: selectedCategory === cat ? '2px solid #2563eb' : '2px solid transparent',
            color: selectedCategory === cat ? '#2563eb' : '#374151'
          }}
        >
          {cat}
        </button>
      ))}
    </nav>
  );
};

export default CategoryNav;
