import React, { useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faTimes, faGift, faShippingFast, faPercent, faTrophy } from '@fortawesome/free-solid-svg-icons';

const CartSidebar = ({ open, onClose, cartItems, setCartItems }) => {
  const [showSubscription, setShowSubscription] = useState(false);

  if (!open) return null;

  const updateQuantity = (id, size, name, delta) => {
    const updated = cartItems.map(item =>
      item.id === id && item.size === size && item.name === name
        ? { ...item, quantity: Math.max(1, item.quantity + delta) }
        : item
    );
    setCartItems(updated);
  };

  const removeItem = (id, size, name) => {
    setCartItems(cartItems.filter(item => !(item.id === id && item.size === size && item.name === name)));
  };

  const subtotal = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
  const itemCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  // Calculate discounts based on quantity
  const getDiscountPercentage = () => {
    if (itemCount >= 10) return 60;
    if (itemCount >= 5) return 25;
    if (itemCount >= 3) return 15;
    return 0;
  };

  const discountPercent = getDiscountPercentage();
  const hasUnlockedPrizes = itemCount >= 3;

  const handleCheckout = async () => {
    const stored = localStorage.getItem('user');
    if (!stored) {
      alert("User not logged in");
      return;
    }

    try {
      await axios.post(
        'http://localhost:3001/api/cart/save',
        { cartItems, total: subtotal },
        { withCredentials: true }
      );

      alert('Order placed successfully!');
      setCartItems([]);
      onClose();
    } catch (err) {
      console.error('❌ Failed to save cart:', err);
      alert('Failed to place order');
    }
  };

  return (
    <div style={overlayStyle}>
      <div style={sidebarStyle}>
        {/* Header */}
        <div style={headerStyle}>
          <h2 style={titleStyle}>MY CART ({itemCount})</h2>
          <button onClick={onClose} style={closeBtn}>
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>

        {/* Rewards Banner */}
        <div style={rewardsBannerStyle}>
          <div style={rewardsProgressStyle}>
            <div style={rewardsBadgeStyle}>
              <FontAwesomeIcon icon={faShippingFast} style={badgeIconStyle} />
              <span>Free Shipping</span>
            </div>
            <div style={rewardsBadgeStyle}>
              <FontAwesomeIcon icon={faPercent} style={badgeIconStyle} />
              <span>3% off</span>
            </div>
            <div style={rewardsBadgeStyle}>
              <FontAwesomeIcon icon={faPercent} style={badgeIconStyle} />
              <span>6% off</span>
            </div>
            <div style={mysteryGiftBadgeStyle}>
              <FontAwesomeIcon icon={faGift} style={badgeIconStyle} />
              <span>+ Mystery Gift</span>
            </div>
          </div>
          <p style={unlockTextStyle}>
            <FontAwesomeIcon icon={faTrophy} style={{ color: '#ff6b35', marginRight: '5px' }} />
            You've unlocked all prizes! 🎉
          </p>
        </div>

        {/* Subscription Banner */}
        <div style={subscriptionBannerStyle}>
          <p style={subscriptionTextStyle}>
            Subscribe to <span style={highlightStyle}>save 20%</span> now and on future deliveries.
          </p>
          <button style={setUpNowBtnStyle} onClick={() => setShowSubscription(!showSubscription)}>
            🔄 Set Up Now
          </button>
        </div>

        {/* Cart Items */}
        <div style={itemsContainerStyle}>
          {cartItems.length === 0 ? (
            <p style={emptyCartStyle}>Your cart is empty.</p>
          ) : (
            cartItems.map((item, index) => (
              <div key={`${item.id}-${item.size}-${item.name}-${index}`} style={itemCardStyle}>
                <div style={itemHeaderStyle}>
                  <img
                    src={item.image}
                    alt={item.name}
                    style={productImageStyle}
                  />
                  <div style={itemInfoStyle}>
                    <h4 style={itemNameStyle}>{item.name}</h4>
                    <span style={sizeTextStyle}>Size: {item.size}</span>
                    <div style={priceContainerStyle}>
                      <span style={currentPriceStyle}>₹{item.price.toFixed(2)}</span>
                      {discountPercent > 0 && (
                        <span style={originalPriceStyle}>₹{(item.price * 1.2).toFixed(2)}</span>
                      )}
                    </div>
                  </div>
                  <div style={quantityControlsRightStyle}>
                    <div style={quantityRowStyle}>
                      <button onClick={() => updateQuantity(item.id, item.size, item.name, -1)} style={qtyBtnStyle}>-</button>
                      <span style={qtyCountStyle}>{item.quantity}</span>
                      <button onClick={() => updateQuantity(item.id, item.size, item.name, 1)} style={qtyBtnStyle}>+</button>
                    </div>
                    <button onClick={() => removeItem(item.id, item.size, item.name)} style={removeTextStyle}>
                      Remove
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>

        {/* Recommended Products */}
        {cartItems.length > 0 && (
          <div style={recommendedSectionStyle}>
            <h4 style={recommendedTitleStyle}>YOUR ORDER GOES GREAT WITH</h4>
            <div style={recommendedItemStyle}>
              <img src="https://via.placeholder.com/60x60" alt="Recommended" style={recommendedImageStyle} />
              <div>
                <p style={recommendedNameStyle}>Premium Add-on Product</p>
                <p style={recommendedPriceStyle}>₹99</p>
              </div>
              <button style={addToOrderBtnStyle}>Add to Order</button>
            </div>
          </div>
        )}

        {/* Footer */}
        {cartItems.length > 0 && (
          <div style={footerStyle}>
            <div style={subtotalRowStyle}>
              <div style={subtotalContainerStyle}>
                <span style={subtotalLabelStyle}>Subtotal:</span>
                <span style={subtotalPriceStyle}>₹{subtotal.toFixed(2)}</span>
              </div>
              <button style={checkoutBtnStyle} onClick={handleCheckout}>
                🔒 Checkout
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

