import React, { useEffect, useState, useRef } from 'react';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Add new state for image upload
  const [imagePreview, setImagePreview] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const fileInputRef = useRef(null);

  const [newProduct, setNewProduct] = useState({
    name: '',
    size: '',
    style: '',
    category: '',
    price: '',
    quantity: '',
    image: '',
  });

  const [totalProducts, setTotalProducts] = useState(0);
  const [activeProducts, setActiveProducts] = useState(0);
  const [lowStock, setLowStock] = useState(0);
  const [totalValue, setTotalValue] = useState(0);

  const [showDeletePopup, setShowDeletePopup] = useState(false);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);

  useEffect(() => {
    fetchProducts();

    // Close dropdown when clicking outside
    const handleClickOutside = (event) => {
      if (showProfileDropdown && !event.target.closest('.profile-dropdown-container')) {
        setShowProfileDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [fetchProducts, showProfileDropdown]);

  const fetchProducts = async () => {
    try {
      const res = await fetch('http://localhost:3001/api/products/all');
      const data = await res.json();
      setProducts(data);
      calculateStats(data);
    } catch (err) {
      console.error('Failed to fetch products', err);
    }
  };

  const calculateStats = (products) => {
    setTotalProducts(products.length);
    const active = products.filter((p) => p.quantity >= 1);
    setActiveProducts(active.length);
    const lowStockCount = products.filter((p) => p.quantity > 0 && p.quantity < 10).length;
    setLowStock(lowStockCount);
    const total = products.reduce((acc, product) => acc + product.price * product.quantity, 0);
    setTotalValue(total);
  };

  const handleDeleteClick = (id) => {
    setSelectedProductId(id);
    setShowDeletePopup(true);
  };

  const confirmDelete = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/products/delete/${selectedProductId}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        fetchProducts();
      } else {
        console.error('Delete failed');
      }
    } catch (err) {
      console.error('Delete failed', err);
    } finally {
      setShowDeletePopup(false);
      setSelectedProductId(null);
    }
  };

  const cancelDelete = () => {
    setShowDeletePopup(false);
    setSelectedProductId(null);
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setNewProduct(product);
    setShowModal(true);
    // Clear image upload states when editing
    setImagePreview(null);
    setSelectedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Updated handleInputChange to clear file upload when URL is entered
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });

    // If image URL is being entered, clear file upload
    if (name === 'image' && value) {
      setImagePreview(null);
      setSelectedFile(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  // New image upload handlers
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file type
      const validTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif', 'image/webp'];
      if (!validTypes.includes(file.type)) {
        alert('Please select a valid image file (JPEG, PNG, GIF, WebP)');
        return;
      }

      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        alert('File size should be less than 5MB');
        return;
      }

      setSelectedFile(file);

      // Create preview URL
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);

      // Clear URL input when file is uploaded
      setNewProduct(prev => ({ ...prev, image: '' }));
    }
  };

  const removeImage = () => {
    setImagePreview(null);
    setSelectedFile(null);
    setNewProduct(prev => ({ ...prev, image: '' }));
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Updated handleSave to handle file uploads
  const handleSave = async () => {
    try {
      let imageUrl = newProduct.image;

      // If there's a selected file, upload it first
      if (selectedFile) {
        const formData = new FormData();
        formData.append('image', selectedFile);

        const uploadResponse = await fetch('http://localhost:3001/api/upload', {
          method: 'POST',
          body: formData
        });

        const uploadResult = await uploadResponse.json();

        if (uploadResult.success) {
          imageUrl = uploadResult.imageUrl;
        } else {
          alert('Failed to upload image');
          return;
        }
      }

      // Create product data with image URL
      const productData = {
        ...newProduct,
        image: imageUrl
      };

      let response;
      if (editingProduct) {
        response = await fetch(`http://localhost:3001/api/products/update/${editingProduct._id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(productData)
        });
      } else {
        response = await fetch('http://localhost:3001/api/products/add', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(productData)
        });
      }

      if (response.ok) {
        // Reset form and close modal
        setEditingProduct(null);
        setShowModal(false);
        setNewProduct({ name: '', size: '', style: '', category: '', price: '', quantity: '', image: '' });
        setImagePreview(null);
        setSelectedFile(null);
        fetchProducts();
      } else {
        alert('Failed to save product');
      }
    } catch (err) {
      console.error('Save failed', err);
      alert('Something went wrong while saving!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login'; // or use your routing method
  };

  // Updated openModal function
  const openModal = () => {
    setEditingProduct(null);
    setNewProduct({ name: '', size: '', style: '', category: '', price: '', quantity: '', image: '' });
    setImagePreview(null);
    setSelectedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
    setShowModal(true);
  };

  const inputStyle = {
    padding: '8px 12px',
    borderRadius: '4px',
    border: '1px solid #d1d5db',
    fontSize: '13px',
    fontFamily: 'system-ui',
  };

  return (
    <div style={{ 
      padding: '20px 32px', 
      backgroundColor: '#f8fafc', 
      minHeight: '100vh', 
      fontFamily: 'system-ui',
      fontSize: '14px'
    }}>
      {/* Topbar */}
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '32px'
      }}>
        <h1 style={{ 
          fontSize: '20px', 
          fontWeight: '600',
          margin: '0',
          color: '#1f2937'
        }}>
          <span style={{ marginRight: '8px' }}>📦</span>Admin Dashboard
        </h1>
        <div style={{ display: 'flex', alignItems: 'center', gap: '12px', position: 'relative' }} className="profile-dropdown-container">
          <span style={{ fontSize: '14px', color: '#6b7280' }}>Welcome, Admin</span>
          <div 
            onClick={() => setShowProfileDropdown(!showProfileDropdown)}
            style={{ 
              width: '32px', 
              height: '32px', 
              backgroundColor: '#3b82f6', 
              color: '#fff', 
              borderRadius: '50%', 
              display: 'flex', 
              alignItems: 'center', 
              justifyContent: 'center', 
              fontWeight: '500',
              cursor: 'pointer',
              fontSize: '14px'
            }}
          >
            A
          </div>

          {/* Profile Dropdown */}
          {showProfileDropdown && (
            <div style={{
              position: 'absolute',
              top: '100%',
              right: '0',
              marginTop: '8px',
              backgroundColor: '#fff',
              border: '1px solid #e5e7eb',
              borderRadius: '6px',
              boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
              zIndex: 1000,
              minWidth: '120px'
            }}>
              <button
                onClick={handleLogout}
                style={{
                  width: '100%',
                  padding: '10px 12px',
                  backgroundColor: 'transparent',
                  border: 'none',
                  textAlign: 'left',
                  cursor: 'pointer',
                  fontSize: '13px',
                  borderRadius: '6px',
                  color: '#374151'
                }}
                onMouseEnter={(e) => e.target.style.backgroundColor = '#f3f4f6'}
                onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
              >
                🚪 Logout
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Stats */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(4, 1fr)', 
        gap: '20px', 
        marginBottom: '32px' 
      }}>
        {[
          { label: 'Total Products', value: totalProducts, icon: '📦', color: '#f97316' },
          { label: 'Active Products', value: activeProducts, icon: '✅', color: '#10b981' },
          { label: 'Low Stock', value: lowStock, icon: '⚠️', color: '#eab308' },
          { label: 'Total Value', value: `$${totalValue.toLocaleString()}`, icon: '💰', color: '#8b5cf6' }
        ].map((stat, idx) => (
          <div key={idx} style={{ 
            backgroundColor: '#fff', 
            padding: '20px', 
            borderRadius: '8px',
            border: '1px solid #f1f5f9',
            boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '8px' }}>
              <span style={{ 
                fontSize: '13px', 
                color: '#6b7280',
                fontWeight: '500'
              }}>{stat.label}</span>
              <div style={{
                width: '32px',
                height: '32px',
                backgroundColor: `${stat.color}20`,
                borderRadius: '6px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '16px'
              }}>
                {stat.icon}
              </div>
            </div>
            <div style={{ 
              fontSize: '24px', 
              fontWeight: '700',
              color: '#111827'
            }}>{stat.value}</div>
          </div>
        ))}
      </div>

      {/* Product Table */}
      <div style={{ 
        backgroundColor: '#fff', 
        borderRadius: '8px', 
        border: '1px solid #f1f5f9',
        boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)',
        overflow: 'hidden'
      }}>
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          padding: '20px 24px',
          borderBottom: '1px solid #f1f5f9'
        }}>
          <h3 style={{ 
            fontSize: '18px', 
            fontWeight: '600',
            margin: '0',
            color: '#111827'
          }}>Product Management</h3>
          <button
            onClick={openModal}
            style={{ 
              backgroundColor: '#3b82f6', 
              color: '#fff', 
              padding: '8px 16px', 
              borderRadius: '6px',
              border: 'none',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '500'
            }}
          >
            + Add New Product
          </button>
        </div>

        <div style={{ overflowX: 'auto' }}>
          <table style={{ width: '100%', fontSize: '13px', borderCollapse: 'collapse', minWidth: '900px' }}>
            <thead>
              <tr style={{ backgroundColor: '#f8fafc' }}>
                <th style={{ 
                  padding: '12px 24px', 
                  textAlign: 'left',
                  fontWeight: '500',
                  color: '#6b7280',
                  textTransform: 'uppercase',
                  fontSize: '11px',
                  letterSpacing: '0.5px'
                }}>PRODUCT</th>
                <th style={{ 
                  padding: '12px 16px', 
                  textAlign: 'center',
                  fontWeight: '500',
                  color: '#6b7280',
                  textTransform: 'uppercase',
                  fontSize: '11px',
                  letterSpacing: '0.5px'
                }}>CATEGORY</th>
                <th style={{ 
                  padding: '12px 16px', 
                  textAlign: 'center',
                  fontWeight: '500',
                  color: '#6b7280',
                  textTransform: 'uppercase',
                  fontSize: '11px',
                  letterSpacing: '0.5px'
                }}>PRICE</th>
                <th style={{ 
                  padding: '12px 16px', 
                  textAlign: 'center',
                  fontWeight: '500',
                  color: '#6b7280',
                  textTransform: 'uppercase',
                  fontSize: '11px',
                  letterSpacing: '0.5px'
                }}>STOCK</th>
                <th style={{ 
                  padding: '12px 16px', 
                  textAlign: 'center',
                  fontWeight: '500',
                  color: '#6b7280',
                  textTransform: 'uppercase',
                  fontSize: '11px',
                  letterSpacing: '0.5px'
                }}>SIZE</th>
                <th style={{ 
                  padding: '12px 16px', 
                  textAlign: 'center',
                  fontWeight: '500',
                  color: '#6b7280',
                  textTransform: 'uppercase',
                  fontSize: '11px',
                  letterSpacing: '0.5px'
                }}>STYLE</th>
                <th style={{ 
                  padding: '12px 16px', 
                  textAlign: 'center',
                  fontWeight: '500',
                  color: '#6b7280',
                  textTransform: 'uppercase',
                  fontSize: '11px',
                  letterSpacing: '0.5px'
                }}>STATUS</th>
                <th style={{ 
                  padding: '12px 24px', 
                  textAlign: 'center',
                  fontWeight: '500',
                  color: '#6b7280',
                  textTransform: 'uppercase',
                  fontSize: '11px',
                  letterSpacing: '0.5px'
                }}>ACTIONS</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product, index) => (
                <tr key={product._id} style={{ 
                  borderBottom: '1px solid #f1f5f9',
                  backgroundColor: index % 2 === 0 ? '#fff' : '#fefefe'
                }}>
                  <td style={{ padding: '16px 24px' }}>
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                      <div style={{
                        width: '32px',
                        height: '32px',
                        borderRadius: '6px',
                        backgroundColor: '#f3f4f6',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        marginRight: '12px',
                        fontSize: '14px'
                      }}>
                        {product.image ? (
                          <img
                            src={product.image}
                            alt={product.name}
                            style={{
                              width: '32px',
                              height: '32px',
                              objectFit: 'cover',
                              borderRadius: '6px'
                            }}
                          />
                        ) : (
                          '📦'
                        )}
                      </div>
                      <div>
                        <div style={{ 
                          fontWeight: '500', 
                          color: '#111827',
                          fontSize: '14px',
                          marginBottom: '2px'
                        }}>{product.name}</div>
                        <div style={{ 
                          fontSize: '12px', 
                          color: '#6b7280'
                        }}>High-quality wireless headphones...</div>
                      </div>
                    </div>
                  </td>
                  <td style={{ 
                    textAlign: 'center', 
                    padding: '16px',
                    color: '#374151',
                    fontSize: '13px'
                  }}>{product.category}</td>
                  <td style={{ 
                    textAlign: 'center', 
                    padding: '16px',
                    color: '#111827',
                    fontWeight: '500',
                    fontSize: '13px'
                  }}>${product.price?.toFixed(2)}</td>
                  <td style={{ 
                    textAlign: 'center', 
                    padding: '16px',
                    color: product.quantity === 0 ? '#dc2626' : '#111827',
                    fontWeight: '500',
                    fontSize: '13px'
                  }}>{product.quantity}</td>
                  <td style={{ 
                    textAlign: 'center', 
                    padding: '16px',
                    color: '#374151',
                    fontSize: '13px'
                  }}>{product.size}</td>
                  <td style={{ 
                    textAlign: 'center', 
                    padding: '16px',
                    color: '#374151',
                    fontSize: '13px'
                  }}>{product.style}</td>
                  <td style={{ textAlign: 'center', padding: '16px' }}>
                    <span style={{
                      padding: '4px 12px',
                      borderRadius: '12px',
                      fontSize: '11px',
                      fontWeight: '500',
                      textTransform: 'lowercase',
                      backgroundColor: product.quantity >= 1 ? '#dcfce7' : '#fee2e2',
                      color: product.quantity >= 1 ? '#166534' : '#dc2626'
                    }}>
                      {product.quantity >= 1 ? 'active' : 'inactive'}
                    </span>
                  </td>
                  <td style={{ textAlign: 'center', padding: '16px 24px' }}>
                    <div style={{ display: 'flex', justifyContent: 'center', gap: '8px' }}>
                      <button
                        onClick={() => handleEdit(product)}
                        style={{
                          background: 'none',
                          border: 'none',
                          cursor: 'pointer',
                          padding: '4px',
                          color: '#3b82f6',
                          fontSize: '16px'
                        }}
                      >
                        ✏️
                      </button>
                      <button
                        onClick={() => handleDeleteClick(product._id)}
                        style={{
                          background: 'none',
                          border: 'none',
                          cursor: 'pointer',
                          padding: '4px',
                          color: '#dc2626',
                          fontSize: '16px'
                        }}
                      >
                        🗑️
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Updated Modal with Image Upload */}
      {showModal && (
        <div style={{
          position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh',
          backgroundColor: 'rgba(0,0,0,0.4)',
          display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000
        }}>
          <div style={{
            backgroundColor: '#fff', 
            padding: '24px', 
            borderRadius: '8px',
            width: '400px', 
            maxHeight: '90vh', 
            overflowY: 'auto', 
            boxShadow: '0 10px 25px rgba(0,0,0,0.1)'
          }}>
            <h3 style={{ 
              fontSize: '18px', 
              fontWeight: 'bold', 
              marginBottom: '16px',
              margin: '0 0 16px 0'
            }}>
              {editingProduct ? 'Edit Product' : 'Add New Product'}
            </h3>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
              <input 
                name="name" 
                placeholder="Product Name" 
                value={newProduct.name} 
                onChange={handleInputChange} 
                style={{...inputStyle, width: '100%', boxSizing: 'border-box'}} 
              />

              {/* Image Upload Section */}
              <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
                <label style={{ fontSize: '14px', fontWeight: '500', color: '#374151' }}>
                  Product Image
                </label>

                {/* Image Preview */}
                {(newProduct.image || imagePreview) && (
                  <div style={{ position: 'relative', width: '100px', height: '100px', marginBottom: '8px' }}>
                    <img 
                      src={imagePreview || newProduct.image} 
                      alt="Product preview" 
                      style={{ 
                        width: '100%', 
                        height: '100%', 
                        objectFit: 'cover', 
                        borderRadius: '4px',
                        border: '1px solid #d1d5db'
                      }} 
                    />
                    <button
                      type="button"
                      onClick={removeImage}
                      style={{
                        position: 'absolute',
                        top: '-8px',
                        right: '-8px',
                        backgroundColor: '#ef4444',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '50%',
                        width: '20px',
                        height: '20px',
                        fontSize: '12px',
                        cursor: 'pointer'
                      }}
                    >
                      ×
                    </button>
                  </div>
                )}

                {/* File Upload Input */}
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  style={{ display: 'none' }}
                  ref={fileInputRef}
                />

                <button
                  type="button"
                  onClick={() => fileInputRef.current?.click()}
                  style={{
                    padding: '8px 12px',
                    backgroundColor: '#f3f4f6',
                    border: '1px solid #d1d5db',
                    borderRadius: '4px',
                    fontSize: '14px',
                    cursor: 'pointer',
                    width: '100%'
                  }}
                >
                  📁 Upload Image
                </button>

                {/* Or divider */}
                <div style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  gap: '8px', 
                  fontSize: '12px', 
                  color: '#6b7280',
                  margin: '4px 0' 
                }}>
                  <div style={{ flex: 1, height: '1px', backgroundColor: '#e5e7eb' }}></div>
                  <span>OR</span>
                  <div style={{ flex: 1, height: '1px', backgroundColor: '#e5e7eb' }}></div>
                </div>

                <input 
                  name="image" 
                  placeholder="Or paste image URL" 
                  value={newProduct.image} 
                  onChange={handleInputChange} 
                  style={{...inputStyle, width: '100%', boxSizing: 'border-box'}} 
                />
              </div>

              <select 
                name="category" 
                value={newProduct.category} 
                onChange={handleInputChange} 
                style={{...inputStyle, width: '100%', boxSizing: 'border-box'}}
              >
                <option value="">Select Category</option>
                <option value="Shirt">Shirt</option>
                <option value="Pant">Pant</option>
                <option value="Accesories">Accesories</option>
              </select>
              <input 
                name="price" 
                type="number" 
                placeholder="Price ($)" 
                value={newProduct.price} 
                onChange={handleInputChange} 
                style={{...inputStyle, width: '100%', boxSizing: 'border-box'}} 
              />
              <input 
                name="quantity" 
                type="number" 
                placeholder="Stock Quantity" 
                value={newProduct.quantity} 
                onChange={handleInputChange} 
                style={{...inputStyle, width: '100%', boxSizing: 'border-box'}} 
              />
              <select 
                name="size" 
                value={newProduct.size} 
                onChange={handleInputChange} 
                style={{...inputStyle, width: '100%', boxSizing: 'border-box'}}
              >
                <option value="">Select Size</option>
                <option value="XS">XS</option>
                <option value="S">S</option>
                <option value="M">M</option>
                <option value="L">L</option>
                <option value="XL">XL</option>
              </select>
              <select 
                name="style" 
                value={newProduct.style} 
                onChange={handleInputChange} 
                style={{...inputStyle, width: '100%', boxSizing: 'border-box'}}
              >
                <option value="">Select Style</option>
                <option value="Regular Fit">Regular Fit</option>
                <option value="Oversized Fit">Oversized Fit</option>
              </select>
            </div>

            <div style={{ 
              display: 'flex', 
              justifyContent: 'flex-end', 
              marginTop: '20px', 
              gap: '10px' 
            }}>
              <button 
                onClick={() => setShowModal(false)} 
                style={{ 
                  padding: '8px 16px', 
                  backgroundColor: '#f3f4f6', 
                  borderRadius: '4px', 
                  border: 'none', 
                  cursor: 'pointer'
                }}
              >
                Cancel
              </button>
              <button 
                onClick={handleSave} 
                style={{ 
                  padding: '8px 16px', 
                  backgroundColor: '#2563eb', 
                  color: '#fff', 
                  borderRadius: '4px', 
                  border: 'none', 
                  cursor: 'pointer'
                }}
              >
                {editingProduct ? 'Update' : 'Save Product'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirmation Popup */}
      {showDeletePopup && (
        <div style={{
          position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center',
          justifyContent: 'center', zIndex: 2000
        }}>
          <div style={{
            background: '#fff', 
            padding: '24px 32px', 
            borderRadius: '12px', 
            width: '350px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.2)', 
            textAlign: 'center', 
            fontFamily: 'system-ui'
          }}>
            <h3 style={{ marginBottom: '10px', margin: '0 0 10px 0' }}>🟠 Delete Product</h3>
            <p style={{ 
              marginBottom: '20px',
              margin: '0 0 20px 0'
            }}>
              Are you sure you want to delete this product? This action cannot be undone.
              </p>
             <div style={{ 
              display: 'flex', 
              justifyContent: 'center', 
              gap: '12px' 
            }}>
              <button 
                onClick={cancelDelete} 
                style={{ 
                  padding: '8px 16px', 
                  backgroundColor: '#e5e7eb', 
                  border: 'none', 
                  borderRadius: '8px', 
                  cursor: 'pointer'
                }}
              >
                Cancel
              </button>
              <button 
                onClick={confirmDelete} 
                style={{ 
                  padding: '8px 16px', 
                  backgroundColor: '#ef4444', 
                  color: '#fff', 
                  border: 'none', 
                  borderRadius: '8px', 
                  cursor: 'pointer'
                }}
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
