// src/Components/CartSidebar.js
import React from 'react';

const CartSidebar = ({ open, cartItems, setCartItems, onClose }) => {
  const handleQuantityChange = (id, type) => {
    const updated = cartItems.map((item) =>
      item.id === id
        ? {
            ...item,
            quantity:
              type === 'inc'
                ? item.quantity + 1
                : item.quantity > 1
                ? item.quantity - 1
                : 1,
          }
        : item
    );
    setCartItems(updated);
  };

  const handleRemove = (id) => {
    setCartItems(cartItems.filter((item) => item.id !== id));
  };

  const totalPrice = cartItems.reduce(
    (total, item) => total + item.price * item.quantity,
    0
  );

  if (!open) return null;

  return (
    <div
      style={{
        position: 'fixed',
        top: 0,
        right: 0,
        width: '300px',
        height: '100vh',
        backgroundColor: '#fff',
        borderLeft: '1px solid #ccc',
        padding: '20px',
        zIndex: 1000,
        overflowY: 'auto',
      }}
    >
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <h3>Shopping Cart</h3>
        <button onClick={onClose}>X</button>
      </div>

      {cartItems.map((item) => (
        <div key={item.id} style={{ marginBottom: '20px' }}>
          <div>{item.name}</div>
          <div>
            ${item.price} Ã— {item.quantity}
          </div>
          <div>
            <button onClick={() => handleQuantityChange(item.id, 'dec')}>-</button>
            <span style={{ margin: '0 10px' }}>{item.quantity}</span>
            <button onClick={() => handleQuantityChange(item.id, 'inc')}>+</button>
            <button onClick={() => handleRemove(item.id)}>ðŸ—‘</button>
          </div>
        </div>
      ))}

      <h4>Total: ${totalPrice.toFixed(2)}</h4>

      <button
        style={{
          backgroundColor: 'green',
          color: '#fff',
          padding: '10px',
          width: '100%',
          border: 'none',
          borderRadius: '4px',
          marginTop: '10px',
        }}
      >
        Send Cart via WhatsApp
      </button>
    </div>
  );
};

export default CartSidebar;
