// src/components/AdminDashboardTopbar.js
import React, { useState, useRef, useEffect } from 'react';

const AdminDashboardTopbar = ({ handleLogout }) => {
  const [showLogout, setShowLogout] = useState(false);
  const avatarRef = useRef();

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (avatarRef.current && !avatarRef.current.contains(e.target)) {
        setShowLogout(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px' }}>
      <h2 style={{ fontSize: '24px', fontWeight: 'bold' }}>
        <span role="img" aria-label="dashboard" style={{ marginRight: '8px' }}>ðŸ“¦</span>Admin Dashboard
      </h2>

      <div style={{ display: 'flex', alignItems: 'center', gap: '12px', position: 'relative' }} ref={avatarRef}>
        <span>Welcome, Admin</span>
        <div
          onClick={() => setShowLogout(prev => !prev)}
          style={{
            width: '32px',
            height: '32px',
            backgroundColor: '#2563eb',
            color: '#fff',
            borderRadius: '50%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold',
            cursor: 'pointer',
            userSelect: 'none',
          }}
        >
          A
        </div>

        {showLogout && (
          <div
            style={{
              position: 'absolute',
              top: '48px',
              right: 0,
              backgroundColor: '#fff',
              border: '1px solid #ddd',
              borderRadius: '6px',
              boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
              zIndex: 100,
              padding: '8px',
              minWidth: '100px',
            }}
          >
            <button
              onClick={handleLogout}
              style={{
                width: '100%',
                backgroundColor: '#000',
                color: '#fff',
                padding: '8px',
                borderRadius: '4px',
                border: 'none',
                cursor: 'pointer',
              }}
            >
              Logout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboardTopbar;
