// src/components/Components/UserDetailsSidebar.jsx
import React from "react";

const UserDetailsSidebar = ({ open, onClose, user, onLogout }) => {
  if (!open) return null;

  return (
    <>
      {/* Overlay */}
      <div
        className="fixed inset-0 bg-black bg-opacity-40 z-40"
        onClick={onClose}
      />

      {/* Sidebar Panel */}
      <div className="fixed top-0 right-0 w-72 h-full bg-white shadow-lg z-50 p-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-bold">User Profile</h2>
          <button onClick={onClose} className="text-gray-500 text-xl font-bold">
            &times;
          </button>
        </div>

        <hr className="mb-4" />

        <p><strong>Name:</strong> {user?.name}</p>
        <p><strong>Email:</strong> {user?.email}</p>
        <p><strong>Role:</strong> {user?.role}</p>

        <button
          onClick={onLogout}
          className="mt-6 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700 w-full"
        >
          Logout
        </button>
      </div>
    </>
  );
};

export default UserDetailsSidebar;
