import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '',
    size: '',
    style: '',
    category: '',
    price: '',
    quantity: '',
    image: '',
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/products/all');
      setProducts(res.data);
    } catch (err) {
      console.error('Failed to fetch products:', err.message);
    }
  };

  const handleDeleteAll = async () => {
    try {
      await axios.post('http://localhost:3001/api/products/delete-all');
      fetchProducts();
    } catch (err) {
      console.error('Failed to delete all products:', err.message);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.post('http://localhost:3001/api/products/delete', { id });
      fetchProducts();
    } catch (err) {
      console.error('Failed to delete product:', err.message);
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setNewProduct({ ...product }); // prefill form with selected product
  };

  const handleInputChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      if (
        !newProduct.name ||
        !newProduct.size ||
        !newProduct.style ||
        !newProduct.category ||
        !newProduct.price ||
        !newProduct.quantity ||
        !newProduct.image
      ) {
        alert('Please fill all fields');
        return;
      }

      if (editingProduct) {
        await axios.post('http://localhost:3001/api/products/update', newProduct);
      } else {
        await axios.post('http://localhost:3001/api/products/add', newProduct);
      }

      setEditingProduct(null);
      setNewProduct({
        name: '',
        size: '',
        style: '',
        category: '',
        price: '',
        quantity: '',
        image: '',
      });
      fetchProducts();
    } catch (err) {
      console.error('Save failed:', err.message);
      alert('Something went wrong while saving.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Admin Dashboard</h2>

      <div style={{ marginBottom: '20px' }}>
        <button
          onClick={handleDeleteAll}
          style={{ backgroundColor: 'red', color: 'white', marginRight: '10px' }}
        >
          Delete All Products
        </button>
        <button
          onClick={() => {
            setEditingProduct(null);
            setNewProduct({
              name: '',
              size: '',
              style: '',
              category: '',
              price: '',
              quantity: '',
              image: '',
            });
          }}
          style={{ backgroundColor: 'green', color: 'white' }}
        >
          Add New Product
        </button>
      </div>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Image</th>
            <th>Name</th>
            <th>Size</th>
            <th>Style</th>
            <th>Category</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, idx) => (
            <tr key={idx}>
              <td>
                <img src={product.image} alt={product.name} style={{ height: '60px' }} />
              </td>
              <td>{product.name}</td>
              <td>{product.size}</td>
              <td>{product.style}</td>
              <td>{product.category}</td>
              <td>â‚¹{product.price}</td>
              <td>{product.quantity}</td>
              <td>
                <button
                  onClick={() => handleEdit(product)}
                  style={{ marginRight: '5px' }}
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(product._id)}
                  style={{ backgroundColor: 'red', color: 'white' }}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add/Edit form */}
      <div style={{ marginTop: '30px' }}>
        <h3>{editingProduct ? 'Edit Product' : 'Add Product'}</h3>
        <input name="name" placeholder="Name" value={newProduct.name} onChange={handleInputChange} />
        <input name="size" placeholder="Size" value={newProduct.size} onChange={handleInputChange} />
        <input name="style" placeholder="Style" value={newProduct.style} onChange={handleInputChange} />
        <input name="category" placeholder="Category" value={newProduct.category} onChange={handleInputChange} />
        <input name="price" placeholder="Price" value={newProduct.price} onChange={handleInputChange} />
        <input name="quantity" placeholder="Quantity" value={newProduct.quantity} onChange={handleInputChange} />
        <input name="image" placeholder="Image URL" value={newProduct.image} onChange={handleInputChange} />

        <button
          onClick={handleSave}
          style={{ backgroundColor: 'blue', color: 'white', marginTop: '10px' }}
        >
          {editingProduct ? 'Update' : 'Add'}
        </button>
      </div>
    </div>
  );
};

export default AdminDashboard;
