import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);

  // Fetch all products
  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/products/all');
      setProducts(res.data);
    } catch (error) {
      console.error('Error fetching products:', error.message);
    }
  };

  // Delete all products
  const deleteAllProducts = async () => {
    const confirm = window.confirm('Are you sure you want to delete all products?');
    if (!confirm) return;

    try {
      await axios.delete('http://localhost:3001/api/products/delete-all');
      alert('All products deleted');
      setProducts([]); // Clear the list
    } catch (error) {
      console.error('Error deleting products:', error.message);
      alert('Failed to delete');
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div style={{ padding: '2rem', fontFamily: 'system-ui' }}>
      <h2>Admin Dashboard</h2>
      <button
        onClick={deleteAllProducts}
        style={{ background: '#dc2626', color: '#fff', padding: '10px', borderRadius: '6px', border: 'none', marginBottom: '20px' }}
      >
        Delete All Products
      </button>

      {products.length === 0 ? (
        <p>No products found</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ background: '#f3f4f6' }}>
              <th style={th}>Image</th>
              <th style={th}>Name</th>
              <th style={th}>Size</th>
              <th style={th}>Style</th>
              <th style={th}>Category</th>
              <th style={th}>Price</th>
              <th style={th}>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p, index) => (
              <tr key={index} style={{ textAlign: 'center', borderBottom: '1px solid #e5e7eb' }}>
                <td style={td}>
                  <img src={p.image} alt={p.name} style={{ width: '60px', height: '60px', objectFit: 'cover', borderRadius: '8px' }} />
                </td>
                <td style={td}>{p.name}</td>
                <td style={td}>{p.size}</td>
                <td style={td}>{p.style}</td>
                <td style={td}>{p.category}</td>
                <td style={td}>â‚¹{p.price}</td>
                <td style={td}>{p.quantity}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const th = {
  padding: '10px',
  borderBottom: '2px solid #e5e7eb',
  fontWeight: 'bold',
};

const td = {
  padding: '10px',
};

export default AdminDashboard;
