import React from 'react';

const CartSidebar = ({ cartItems }) => {
  return (
    <div className="cart-sidebar">
      <h3>Your Cart</h3>
      {cartItems.length === 0 ? (
        <p>No items in cart</p>
      ) : (
        cartItems.map((item, idx) => (
          <div key={idx}>
            <p>{item.name} - {item.quantity}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default CartSidebar;
