import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/products');
      setProducts(response.data);
    } catch (err) {
      console.error('Error fetching products', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/products/${id}`);
      fetchProducts(); // Refresh list
    } catch (err) {
      console.error('Error deleting product', err);
    }
  };

  const handleEdit = (product) => {
    alert(`Edit clicked for ${product.name}`);
    // Or navigate to edit page: navigate(`/edit/${product._id}`)
  };

  return (
    <div style={{ padding: '40px', fontFamily: 'Arial, sans-serif', backgroundColor: '#f9fafb', minHeight: '100vh' }}>
      <h2 style={{ fontSize: '28px', fontWeight: 'bold', marginBottom: '30px' }}>Admin Dashboard</h2>

      <div style={{ overflowX: 'auto' }}>
        <table style={{ width: '100%', borderCollapse: 'collapse', backgroundColor: '#fff', borderRadius: '10px', overflow: 'hidden' }}>
          <thead style={{ backgroundColor: '#f3f4f6' }}>
            <tr>
              <th style={thStyle}>Product</th>
              <th style={thStyle}>Price</th>
              <th style={thStyle}>Stock</th>
              <th style={thStyle}>Size</th>
              <th style={thStyle}>Style</th>
              <th style={thStyle}>Status</th>
              <th style={thStyle}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product._id} style={{ borderTop: '1px solid #e5e7eb', verticalAlign: 'middle' }}>
                <td style={{ padding: '12px', display: 'flex', alignItems: 'center', gap: '12px' }}>
                  <img
                    src={product.image}
                    alt={product.name}
                    style={{
                      width: '50px',
                      height: '50px',
                      objectFit: 'cover',
                      borderRadius: '8px',
                      border: '1px solid #ddd',
                    }}
                  />
                  <div>
                    <div style={{ fontWeight: '600' }}>{product.name}</div>
                    <div style={{ fontSize: '12px', color: '#6b7280' }}>{product.category}</div>
                  </div>
                </td>
                <td style={tdCenter}>${product.price.toFixed(2)}</td>
                <td style={tdCenter}>{product.quantity}</td>
                <td style={tdCenter}>{product.size}</td>
                <td style={tdCenter}>{product.style}</td>
                <td style={tdCenter}>
                  <span
                    style={{
                      padding: '4px 10px',
                      borderRadius: '9999px',
                      fontSize: '12px',
                      backgroundColor: product.quantity >= 1 ? '#dcfce7' : '#fee2e2',
                      color: product.quantity >= 1 ? '#15803d' : '#b91c1c',
                      fontWeight: 500,
                    }}
                  >
                    {product.quantity >= 1 ? 'Active' : 'Inactive'}
                  </span>
                </td>
                <td style={tdCenter}>
                  <button
                    onClick={() => handleEdit(product)}
                    style={{
                      background: 'none',
                      border: 'none',
                      color: '#3b82f6',
                      fontSize: '16px',
                      cursor: 'pointer',
                      marginRight: '10px',
                    }}
                    title="Edit"
                  >
                    ‚úèÔ∏è
                  </button>
                  <button
                    onClick={() => handleDelete(product._id)}
                    style={{
                      background: 'none',
                      border: 'none',
                      color: '#ef4444',
                      fontSize: '16px',
                      cursor: 'pointer',
                    }}
                    title="Delete"
                  >
                    üóëÔ∏è
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

