import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
  const [allProducts, setAllProducts] = useState([]); // Full list from backend
  const [products, setProducts] = useState([]); // Filtered list
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '',
    size: '',
    style: '',
    quantity: '',
    price: '',
    category: ''
  });

  // Filter states
  const [categoryFilter, setCategoryFilter] = useState('');
  const [sizeFilter, setSizeFilter] = useState('');
  const [styleFilter, setStyleFilter] = useState('');

  const navigate = useNavigate();

  // Fetch all products
  const fetchProducts = async () => {
    try {
      const res = await fetch('http://localhost:3001/api/products/all');
      const data = await res.json();
      setAllProducts(data); // keep full copy
      setProducts(data); // initially show all
    } catch (err) {
      console.error('Failed to fetch products', err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Apply filters
  const applyFilters = () => {
    let filtered = allProducts;

    if (categoryFilter) {
      filtered = filtered.filter((p) => p.category === categoryFilter);
    }
    if (sizeFilter) {
      filtered = filtered.filter((p) => p.size === sizeFilter);
    }
    if (styleFilter) {
      filtered = filtered.filter((p) => p.style === styleFilter);
    }

    setProducts(filtered);
  };

  // Run filters whenever filter states change
  useEffect(() => {
    applyFilters();
  }, [categoryFilter, sizeFilter, styleFilter]);

  // Delete product
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/products/${id}`);
      fetchProducts();
    } catch (err) {
      console.error('Failed to delete product', err);
    }
  };

  // Edit product
  const handleEditChange = (e) => {
    setEditingProduct({ ...editingProduct, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(
        `http://localhost:3001/api/products/${editingProduct._id}`,
        editingProduct
      );
      setEditingProduct(null);
      fetchProducts();
    } catch (err) {
      console.error('Failed to update product', err);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Admin Dashboard</h2>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>
              <select
                value={categoryFilter}
                onChange={(e) => setCategoryFilter(e.target.value)}
                style={{
                  padding: '4px',
                  fontSize: '12px',
                  borderRadius: '4px',
                  border: '1px solid #ccc'
                }}
              >
                <option value="">Category</option>
                <option value="Shirt">Shirt</option>
                <option value="Pant">Pant</option>
                <option value="Accesories">Accesories</option>
              </select>
            </th>
            <th>
              <select
                value={sizeFilter}
                onChange={(e) => setSizeFilter(e.target.value)}
                style={{
                  padding: '4px',
                  fontSize: '12px',
                  borderRadius: '4px',
                  border: '1px solid #ccc'
                }}
              >
                <option value="">Size</option>
                <option value="S">S</option>
                <option value="M">M</option>
                <option value="L">L</option>
                <option value="XL">XL</option>
              </select>
            </th>
            <th>
              <select
                value={styleFilter}
                onChange={(e) => setStyleFilter(e.target.value)}
                style={{
                  padding: '4px',
                  fontSize: '12px',
                  borderRadius: '4px',
                  border: '1px solid #ccc'
                }}
              >
                <option value="">Style</option>
                <option value="Casual">Casual</option>
                <option value="Formal">Formal</option>
                <option value="Sports">Sports</option>
              </select>
            </th>
            <th>Quantity</th>
            <th>Price</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p._id}>
              <td>{p.category}</td>
              <td>{p.size}</td>
              <td>{p.style}</td>
              <td>{p.quantity}</td>
              <td>{p.price}</td>
              <td
                style={{
                  color: p.quantity >= 1 ? 'green' : 'red',
                  fontWeight: 'bold'
                }}
              >
                {p.quantity >= 1 ? 'Active' : 'Inactive'}
              </td>
              <td>
                <button onClick={() => setEditingProduct(p)}>Edit</button>
                <button onClick={() => handleDelete(p._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editingProduct && (
        <div style={{ marginTop: '20px' }}>
          <h3>Edit Product</h3>
          <input
            name="name"
            value={editingProduct.name}
            onChange={handleEditChange}
            placeholder="Name"
          />
          <input
            name="size"
            value={editingProduct.size}
            onChange={handleEditChange}
            placeholder="Size"
          />
          <input
            name="style"
            value={editingProduct.style}
            onChange={handleEditChange}
            placeholder="Style"
          />
          <input
            name="quantity"
            value={editingProduct.quantity}
            onChange={handleEditChange}
            placeholder="Quantity"
          />
          <input
            name="price"
            value={editingProduct.price}
            onChange={handleEditChange}
            placeholder="Price"
          />
          <button onClick={handleSave}>Save</button>
          <button onClick={() => setEditingProduct(null)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
