import React, { useEffect, useState } from 'react';
import { Package, ShoppingCart, AlertTriangle, DollarSign, Plus, Edit2, Trash2, LogOut } from 'lucide-react';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '', size: '', style: '', category: '', price: '', quantity: '', image: ''
  });

  // Mock data for demonstration
  useEffect(() => {
    // Simulating API call with mock data
    const mockProducts = [
      {
        _id: '1',
        name: 'Wireless Headphones',
        category: 'Electronics',
        price: 99.99,
        quantity: 25,
        size: 'M',
        style: 'Regular Fit',
        image: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=100&h=100&fit=crop',
        status: 'active'
      },
      {
        _id: '2',
        name: 'Cotton T-Shirt',
        category: 'Clothing',
        price: 24.99,
        quantity: 50,
        size: 'L',
        style: 'Oversized Fit',
        image: 'https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?w=100&h=100&fit=crop',
        status: 'active'
      },
      {
        _id: '3',
        name: 'JavaScript Guide',
        category: 'Books',
        price: 39.99,
        quantity: 15,
        size: 'M',
        style: 'Regular Fit',
        image: 'https://images.unsplash.com/photo-1543002588-bfa74002ed7e?w=100&h=100&fit=crop',
        status: 'active'
      },
      {
        _id: '4',
        name: 'Garden Tools Set',
        category: 'Home & Garden',
        price: 79.99,
        quantity: 8,
        size: 'L',
        style: 'Regular Fit',
        image: 'https://images.unsplash.com/photo-1416879595882-3373a0480b5b?w=100&h=100&fit=crop',
        status: 'active'
      },
      {
        _id: '5',
        name: 'Running Shoes',
        category: 'Sports',
        price: 129.99,
        quantity: 0,
        size: 'S',
        style: 'Regular Fit',
        image: 'https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=100&h=100&fit=crop',
        status: 'inactive'
      }
    ];
    setProducts(mockProducts);
  }, []);

  const totalProducts = products.length;
  const activeProducts = products.filter(p => p.status === 'active').length;
  const lowStockProducts = products.filter(p => p.quantity <= 10).length;
  const totalValue = products.reduce((sum, p) => sum + (p.price * p.quantity), 0);

  const handleDeleteAll = async () => {
    if (window.confirm('Are you sure you want to delete all products?')) {
      setProducts([]);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      setProducts(products.filter(p => p._id !== id));
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setNewProduct(product);
  };

  const handleInputChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      if (editingProduct) {
        setProducts(products.map(p => p._id === editingProduct._id ? { ...newProduct, _id: editingProduct._id, status: 'active' } : p));
      } else {
        const id = Date.now().toString();
        setProducts([...products, { ...newProduct, _id: id, status: 'active' }]);
      }
      setEditingProduct(null);
      setNewProduct({ name: '', size: '', style: '', category: '', price: '', quantity: '', image: '' });
    } catch (err) {
      console.error('Save failed', err);
      alert('Something went wrong while saving!');
    }
  };

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      console.log('Logging out...');
    }
  };

  const getCategoryIcon = (category) => {
    const iconMap = {
      'Electronics': 'ðŸ“±',
