import React from 'react';

const ProductGrid = ({ products }) => {
  return (
    <div className="product-grid">
      {products.map((product, idx) => (
        <div className="product-card" key={idx}>
          <img src={product.image} alt={product.name} />
          <h4>{product.name}</h4>
          <p>{product.price}</p>
        </div>
      ))}
    </div>
  );
};

export default ProductGrid;
