import React from 'react';
import { useNavigate } from 'react-router-dom';

const UserDetailsSidebar = ({ open, onClose }) => {
  const navigate = useNavigate();

  if (!open) return null;

  const role = localStorage.getItem('role');
  const name = localStorage.getItem('name') || 'Admin';
  const email = localStorage.getItem('email') || 'admin@example.com';

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <>
      {/* Overlay */}
      <div
        className="fixed inset-0 bg-black bg-opacity-40 z-40"
        onClick={onClose}
      ></div>

      {/* Sidebar */}
      <div
        className="fixed top-0 right-0 h-full w-64 bg-white shadow-lg p-4 z-50"
        style={{ borderLeft: '1px solid #ccc' }}
      >
        <button
          onClick={onClose}
          style={{
            float: 'right',
            fontSize: '20px',
            fontWeight: 'bold',
            border: 'none',
            background: 'none',
            cursor: 'pointer',
          }}
        >
          Ã—
        </button>

        <h3>User Profile</h3>
        <hr />
        <p><strong>Name:</strong> {name}</p>
        <p><strong>Email:</strong> {email}</p>
        <p><strong>Role:</strong> {role}</p>

        <button
          onClick={handleLogout}
          style={{
            marginTop: '20px',
            backgroundColor: 'red',
            color: 'white',
            border: 'none',
            padding: '10px 15px',
            cursor: 'pointer',
            borderRadius: '4px'
          }}
        >
          Logout
        </button>
      </div>
    </>
  );
};

export default UserDetailsSidebar;
