import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '', size: '', style: '', category: '', price: '', quantity: '', image: ''
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await axios.get('http://localhost:3001/api/products/all');
    setProducts(res.data);
  };

  const handleDeleteAll = async () => {
    try {
      await axios.delete('http://localhost:3001/api/products/delete-all');
      fetchProducts();
    } catch (err) {
      console.error('Delete all failed', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/products/delete/${id}`);
      fetchProducts();
    } catch (err) {
      console.error('Delete failed', err);
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setNewProduct(product);
  };

  const handleInputChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      if (editingProduct) {
        await axios.put(`http://localhost:3001/api/products/update/${editingProduct._id}`, newProduct);
      } else {
        await axios.post('http://localhost:3001/api/products/add', newProduct);
      }
      setEditingProduct(null);
      setNewProduct({ name: '', size: '', style: '', category: '', price: '', quantity: '', image: '' });
      fetchProducts();
    } catch (err) {
      console.error('Save failed', err);
      alert('Something went wrong while saving!');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Admin Dashboard</h2>
      <button onClick={handleDeleteAll} style={{ backgroundColor: 'red', color: 'white', marginBottom: '20px' }}>
        Delete All Products
      </button>
      <button onClick={() => setEditingProduct(null)} style={{ backgroundColor: 'green', color: 'white', marginLeft: '10px' }}>
        Add New Product
      </button>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Image</th><th>Name</th><th>Size</th><th>Style</th><th>Category</th><th>Price</th><th>Quantity</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, idx) => (
            <tr key={idx}>
              <td><img src={product.image} alt={product.name} style={{ height: '60px' }} /></td>
              <td>{product.name}</td>
              <td>{product.size}</td>
              <td>{product.style}</td>
              <td>{product.category}</td>
              <td>₹{product.price}</td>
              <td>{product.quantity}</td>
              <td>
                <button onClick={() => handleEdit(product)} style={{ marginRight: '5px' }}>Edit</button>
                <button onClick={() => handleDelete(product._id)} style={{ backgroundColor: 'red', color: 'white' }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add/Edit form */}
      <div style={{ marginTop: '30px' }}>
        <h3>{editingProduct ? 'Edit Product' : 'Add Product'}</h3>
        <input name="name" placeholder="Name" value={newProduct.name} onChange={handleInputChange} />
        <input name="size" placeholder="Size" value={newProduct.size} onChange={handleInputChange} />
        <input name="style" placeholder="Style" value={newProduct.style} onChange={handleInputChange} />
        <input name="category" placeholder="Category" value={newProduct.category} onChange={handleInputChange} />
        <input name="price" placeholder="Price" value={newProduct.price} onChange={handleInputChange} />
        <input name="quantity" placeholder="Quantity" value={newProduct.quantity} onChange={handleInputChange} />
        <input name="image" placeholder="Image URL" value={newProduct.image} onChange={handleInputChange} />
        <button onClick={handleSave} style={{ backgroundColor: 'blue', color: 'white', marginTop: '10px' }}>
          {editingProduct ? 'Update' : 'Add'}
        </button>
      </div>
    </div>
  );
};

export default AdminDashboard;
