import React from 'react';

const ProductGrid = ({ products, category, size, style, onAddToCart }) => {
  const filteredProducts = products.filter((product) => {
    const matchCategory = category ? product.category === category : true;
    const matchSize = size ? product.size === size : true;
    const matchStyle = style ? product.style === style : true;
    return matchCategory && matchSize && matchStyle;
  });

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 40px' }}>
      {filteredProducts.length === 0 ? (
        <p style={{ fontSize: '16px', color: '#555' }}>
          No products match the selected filters.
        </p>
      ) : (
        <div
          style={{
            display: 'flex',
            gap: '24px',
            flexWrap: 'wrap',
            justifyContent: 'flex-start',
          }}
        >
          {filteredProducts.map((product) => (
            <div
              key={product._id}
              style={{
                border: '1px solid #e5e7eb',
                borderRadius: '12px',
                padding: '16px',
                width: '220px',
                textAlign: 'left',
                backgroundColor: '#fff',
                boxShadow: '0 4px 6px rgba(0,0,0,0.08)',
              }}
            >
              <div
                style={{
                  backgroundColor: product.color,
                  height: '160px',
                  marginBottom: '12px',
                  borderRadius: '8px',
                }}
              ></div>
              <h4 style={{ fontSize: '15px', fontWeight: '600', color: '#1f2937' }}>
                {product.name}
              </h4>
              <p style={{ fontSize: '13px', color: '#6b7280' }}>
                <strong>Size:</strong> {product.size}
              </p>
              <p
                style={{
                  fontWeight: '600',
                  fontSize: '14px',
                  color: '#2563eb',
                  marginBottom: '16px',
                }}
              >
                ${product.price}
              </p>
              <button
                onClick={() => onAddToCart(product)}
                style={{
                  backgroundColor: '#2563eb',
                  color: '#fff',
                  border: 'none',
                  padding: '10px 0',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontWeight: '600',
                  width: '100%',
                }}
              >
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProductGrid;
