import React from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';

const CartSidebar = ({ open, onClose, cartItems, setCartItems }) => {
  if (!open) return null;

  const updateQuantity = (id, size, name, delta) => {
    const updated = cartItems.map(item =>
      item.id === id && item.size === size && item.name === name
        ? { ...item, quantity: Math.max(1, item.quantity + delta) }
        : item
    );
    setCartItems(updated);
  };

  const removeItem = (id, size, name) => {
    setCartItems(cartItems.filter(item => !(item.id === id && item.size === size && item.name === name)));
  };

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleSaveCart = async () => {
    const stored = localStorage.getItem('user');
    if (!stored) {
      alert("User not logged in");
      return;
    }

    try {
      await axios.post(
        'http://localhost:3001/api/cart/save',
        { cartItems, total },
        { withCredentials: true }
      );

      alert('Cart saved successfully!');
      setCartItems([]);
      onClose();
    } catch (err) {
      console.error('‚ùå Failed to save cart:', err);
      alert('Failed to save cart');
    }
  };

  return (
    <div style={overlayStyle}>
      <div style={sidebarStyle} onClick={(e) => e.stopPropagation()}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
          <h3 style={{ margin: 0 }}>üõí Cart</h3>
          <button onClick={onClose} style={closeBtn}>√ó</button>
        </div>

        {cartItems.length === 0 ? (
          <p style={{ textAlign: 'center' }}>Your cart is empty.</p>
        ) : (
          cartItems.map((item, index) => (
            <div key={`${item.id}-${item.size}-${item.name}-${index}`} style={itemStyle}>
              <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
                <img
                  src={item.image}
                  alt={item.name}
                  style={{ width: '50px', height: '50px', objectFit: 'cover', borderRadius: '6px' }}
                />
                <div style={{ flex: 1 }}>
                  <p style={{ fontWeight: 'bold', margin: 0 }}>{item.name}</p>
                  <p style={{ margin: 0, fontSize: '0.85rem' }}>Size: {item.size}</p>
                  <p style={{ margin: 0, fontSize: '0.85rem' }}>Style: {item.style}</p>
                  <p style={{ margin: 0, fontSize: '0.9rem' }}>‚Çπ{item.price.toFixed(2)}</p>
                </div>
              </div>

              <div style={{ display: 'flex', alignItems: 'center', marginTop: '6px' }}>
                <button onClick={() => updateQuantity(item.id, item.size, item.name, -1)} style={qtyBtn}>‚àí</button>
                <span style={{ margin: '0 10px' }}>{item.quantity}</span>
                <button onClick={() => updateQuantity(item.id, item.size, item.name, 1)} style={qtyBtn}>+</button>

                <button onClick={() => removeItem(item.id, item.size, item.name)} style={deleteBtn}>
                  <FontAwesomeIcon icon={faTrash} style={{ color: '#e11d48' }} />
                </button>
              </div>
            </div>
          ))
        )}

        <div style={{ marginTop: 'auto' }}>
          <h4>Total: ‚Çπ{total.toFixed(2)}</h4>
          <button style={saveBtn} onClick={handleSaveCart}>Add to Cart</button>
        </div>
      </div>
    </div>
  );
};

// Full-screen dark overlay, aligns the sidebar to top-right
