import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { jsxDEV as _jsxDEV } from "react/jsx-dev-runtime";
const AdminDashboard = () => {
  _s();
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '',
    size: '',
    style: '',
    category: '',
    price: '',
    quantity: '',
    image: '',
    status: 'active'
  });
  const [totalProducts, setTotalProducts] = useState(0);
  const [activeProducts, setActiveProducts] = useState(0);
  const [lowStock, setLowStock] = useState(0);
  const [totalValue, setTotalValue] = useState(0);
  const navigate = useNavigate();
  useEffect(() => {
    fetchProducts();
  }, []);
  const fetchProducts = async () => {
    const res = await axios.get('http://localhost:3001/api/products/all');
    setProducts(res.data);
    calculateStats(res.data);
  };
  const calculateStats = products => {
    setTotalProducts(products.length);
    setActiveProducts(products.filter(p => p.status === 'active').length);
    const lowStockCount = products.filter(p => p.quantity < 10).length;
    setLowStock(lowStockCount);
    const total = products.reduce((acc, product) => acc + product.price * product.quantity, 0);
    setTotalValue(total);
  };
  const handleDelete = async id => {
    try {
      await axios.delete(`http://localhost:3001/api/products/delete/${id}`);
