import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '',
    size: '',
    style: '',
    category: '',
    price: '',
    quantity: '',
    image: '',
    status: 'active',
  });

  const [totalProducts, setTotalProducts] = useState(0);
  const [activeProducts, setActiveProducts] = useState(0);
  const [lowStock, setLowStock] = useState(0);
  const [totalValue, setTotalValue] = useState(0);

  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await axios.get('http://localhost:3001/api/products/all');
    setProducts(res.data);
    calculateStats(res.data);
  };

  const calculateStats = (products) => {
    setTotalProducts(products.length);
    setActiveProducts(products.filter(p => p.status === 'active').length);
    const lowStockCount = products.filter(p => p.quantity < 10).length;
    setLowStock(lowStockCount);
    const total = products.reduce((acc, product) => acc + (product.price * product.quantity), 0);
    setTotalValue(total);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/products/delete/${id}`);
      fetchProducts();
    } catch (err) {
      console.error('Delete failed', err);
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setNewProduct(product);
  };

  const handleInputChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      if (editingProduct) {
        await axios.put(`http://localhost:3001/api/products/update/${editingProduct._id}`, newProduct);
      } else {
        await axios.post('http://localhost:3001/api/products/add', newProduct);
      }
      setEditingProduct(null);
      setNewProduct({ name: '', size: '', style: '', category: '', price: '', quantity: '', image: '', status: 'active' });
      fetchProducts();
    } catch (err) {
      console.error('Save failed', err);
      alert('Something went wrong while saving!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen font-sans">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold flex items-center">
          <span role="img" aria-label="dashboard" className="mr-2">📦</span>
          Admin Dashboard
        </h2>
        <div className="flex items-center gap-4">
          <span>Welcome, Admin</span>
          <div className="w-8 h-8 bg-blue-600 text-white rounded-full flex items-center justify-center font-bold">A</div>
          <button onClick={handleLogout} className="bg-black text-white px-4 py-1 rounded">Logout</button>
        </div>
      </div>

      <div className="grid grid-cols-4 gap-4 mb-6">
        <div className="bg-white p-4 rounded shadow">
          <p className="text-sm">Total Products</p>
          <h2 className="text-2xl font-bold">{totalProducts}</h2>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <p className="text-sm">Active Products</p>
          <h2 className="text-2xl font-bold">{activeProducts}</h2>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <p className="text-sm">Low Stock</p>
          <h2 className="text-2xl font-bold">{lowStock}</h2>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <p className="text-sm">Total Value</p>
          <h2 className="text-2xl font-bold">${totalValue.toFixed(2)}</h2>
        </div>
      </div>

      <div className="bg-white p-4 rounded shadow mb-6">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-bold">Product Management</h3>
          <button onClick={() => setEditingProduct(null)} className="bg-blue-600 text-white px-4 py-2 rounded">+ Add New Product</button>
        </div>
        <table className="w-full text-sm">
          <thead className="bg-blue-100">
            <tr>
              <th className="p-2 text-left">Product</th>
              <th>Category</th>
              <th>Price</th>
              <th>Stock</th>
              <th>Size</th>
              <th>Style</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product._id} className="border-t">
                <td className="p-2">{product.name}</td>
                <td className="text-center">{product.category}</td>
                <td className="text-center">${product.price.toFixed(2)}</td>
                <td className="text-center">{product.quantity}</td>
                <td className="text-center">{product.size}</td>
                <td className="text-center">{product.style}</td>
                <td className="text-center">
                  <span className={`px-2 py-1 rounded-full text-xs ${product.status === 'active' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
                    {product.status}
                  </span>
                </td>
                <td className="text-center">
                  <button onClick={() => handleEdit(product)} className="bg-blue-500 text-white px-2 py-1 rounded mr-2">Edit</button>
                  <button onClick={() => handleDelete(product._id)} className="bg-red-500 text-white px-2 py-1 rounded">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="bg-white p-4 rounded shadow">
        <h3 className="text-lg font-bold mb-2">{editingProduct ? 'Edit Product' : 'Add Product'}</h3>
        <div className="grid grid-cols-2 gap-4">
          <input name="name" placeholder="Name" value={newProduct.name} onChange={handleInputChange} className="border p-2 rounded" />
          <input name="category" placeholder="Category" value={newProduct.category} onChange={handleInputChange} className="border p-2 rounded" />
          <input name="price" placeholder="Price" type="number" value={newProduct.price} onChange={handleInputChange} className="border p-2 rounded" />
          <input name="quantity" placeholder="Quantity" type="number" value={newProduct.quantity} onChange={handleInputChange} className="border p-2 rounded" />
          <input name="size" placeholder="Size" value={newProduct.size} onChange={handleInputChange} className="border p-2 rounded" />
          <input name="style" placeholder="Style" value={newProduct.style} onChange={handleInputChange} className="border p-2 rounded" />
          <select name="status" value={newProduct.status} onChange={handleInputChange} className="border p-2 rounded">
            <option value="active">Active</option>
            <option value="inactive">Inactive</option>
          </select>
        </div>
        <button onClick={handleSave} className="mt-4 bg-blue-600 text-white px-4 py-2 rounded">
          {editingProduct ? 'Update' : 'Add'}
        </button>
      </div>
    </div>
  );
};

export default AdminDashboard;
